INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6693, 'Famotidine', 'Pazopanib', 'Major', 'Coadministration with proton pump inhibitors or H2-receptor antagonists may significantly decrease the oral bioavailability of pazopanib and reduce its concentrations in plasma. The solubility of pazopanib is pH-dependent, thus an increase in pH may interfere with its absorption.', NULL, 'Concomitant use of pazopanib with proton pump inhibitors and H2-receptor antagonists should generally be avoided.', NULL, 'Concomitant use of pazopanib with proton pump inhibitors and H2-receptor antagonists should generally be avoided. If acid-suppression therapy is required, short-acting antacids should be considered, with dosing separated by several hours from pazopanib dosing. Similarly, when coadministration with an H2-receptor antagonist is required, it has been recommended that pazopanib be taken without food at least 2 hours before or 10 hours after a dose of the H2-receptor antagonist.', 'Absorption', 'Tinidazole, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Vonoprazan, Amoxicillin', 'Tepotinib, Larotrectinib, Avapritinib, Fedratinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Duvelisib, Tivozanib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6697/', '[1] van Leeuwen RW, van Gelder T, Mathijssen RH, Jansman FG "Drug-drug interactions with tyrosine-kinase inhibitors: a clinical perspective." Lancet Oncol 15 (2014): e315-e326[2] Yu G, Zheng QS, Wang DX, Zhou HH, Li GF "Drug interactions between tyrosine-kinase inhibitors and acid suppressive agents: more than meets the eye." Lancet Oncol 15 (2014): e469-70[3] EMEA. European Medicine