INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6859, 'Ranitidine', 'Oxtriphylline', 'Moderate', 'Cimetidine may increase theophylline plasma concentrations by as much as 70%. The mechanism is related to inhibition of liver CYP450 enzymes responsible for theophylline metabolism. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity.', NULL, 'Clinical monitoring of patient response, tolerance, and laboratory theophylline serum concentrations is recommended.', NULL, 'Clinical monitoring of patient response, tolerance, and laboratory theophylline serum concentrations is recommended. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physician.', 'Metabolism', 'Tinidazole, Pantoprazole, Levofloxacin, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Rifabutin, Esomeprazole, More', 'Omalizumab, Tezepelumab, Benralizumab, Reslizumab, Dyphylline, Mepolizumab, Roflumilast, Bromotheophylline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6863/', '[1] Schwartz JI, Bachmann KA, Bond LW, Mahajan VK "Impact of cimetidine on the pharmacokinetics of the theophylline." Clin Pharm 1 (1982): 534-8[2] Jackson JE, Powell JR, Wandell M, Bentley J, Dorr R "Cimetidine decreases theophylline clearance." Am Rev Respir Dis 123 (1981): 615-7[3] Anderson JR, Poklis A, Slavin RG "A fatal case of theophylline intoxication." Arch Intern Med 143 (1983): 559-60