INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (6969, 'Indinavir', 'Rabeprazole', 'Moderate', 'Proton pump inhibitors may decrease the gastrointestinal absorption of indinavir. The proposed mechanism is a pH-dependent reduction in drug dissolution or absorption. The interaction has been reported specifically with omeprazole but may be expected with other proton pump inhibitors based on their common pharmacologic action.', NULL, 'Caution is advised during the chronic use of these drugs with indinavir due to the risk of treatment failure and emergence of drug resistance associated with subtherapeutic levels of indinavir.', NULL, 'Caution is advised during the chronic use of these drugs with indinavir due to the risk of treatment failure and emergence of drug resistance associated with subtherapeutic levels of indinavir. Patients should be monitored for altered antiviral effectiveness.', 'Absorption', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Telaprevir, Dolutegravir, Oseltamivir, More', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Cimetidine, Nizatidine, Amoxicillin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/6973/', '[1] Burger DM, Hugen PWH, Kroon FP, Groeneveld P, Brinkman K, Foudraine NA, Sprenger H, Koopmans PP, Hekster YA "Pharmacokinetic interaction between the proton pump inhibitor omeprazole and the HIV protease inhibitor indinavir." AIDS 12 (1998): 2080-2[2] Shelton MJ, Wynn HE, Newitt RG, DiFrancesco R "Effects of grapefruit juice on pharmacokinetic exposure to indinavir in HIV-positive subjects." J Clin Pharmacol 41 (2001): 435-42[3] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[4] Yeh KC, Deutsch PJ, Haddix H, Hesney M, Hoagland V, Ju WD, Justice SJ, Osborne B, Sterrett AT, Stone JA, Woolf E, Waldman S "Single-dose pharmacokinetics of indinavir and the effect of food." Antimicrob Agents Chemother 42 (1998): 332-8', 1767369485);
