INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7000, 'Rabeprazole', 'Ponatinib', 'Minor', 'Coadministration with drugs that elevate the gastric pH such as proton pump inhibitors, H2-receptor antagonists, and antacids may decrease the oral bioavailability of ponatinib and reduce its concentrations in plasma. The aqueous solubility of ponatinib has been shown to be pH-dependent, with higher pH resulting in decreased solubility.', NULL, '-', NULL, '-', 'Absorption', 'Tetracycline, Levofloxacin, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Vonoprazan, Amoxicillin, Misoprostol, Chondroitin sulfate, Glucosamine, Rofecoxib, More', 'Entrectinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Avapritinib, Larotrectinib, Vemurafenib, Selumetinib, Ribociclib, Cobimetinib, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7004/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[3] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767369485);
