INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (13002, 'Ethiodized oil', 'Aldesleukin', 'Moderate', 'An increased incidence (12.6%) of acute, atypical reactions to iodinated contrast media has been observed in patients who have been previously treated with interleukin-2. The cause and mechanism have not been established. Symptoms generally have an onset of 1 to 4 hours and may include nausea, vomiting, diarrhea, fever, chills, flu-like symptoms, joint pain, rash, pruritus, hypotension, edema, and oliguria. Reactions have typically been reported when patients received iodinated contrast media within 4 weeks after the last dose of interleukin-2, but have also occurred several months after interleukin treatment.', NULL, 'Patients should be monitored for adverse reactions after receiving iodinated contrast media, and symptomatic treatment initiated as clinically appropriate.', NULL, 'Patients should be monitored for adverse reactions after receiving iodinated contrast media, and symptomatic treatment initiated as clinically appropriate.', 'Others', 'Interferon alfa-n1, Oprelvekin, Sipuleucel-T, Elapegademase, Interferon beta-1b, Peginterferon alfa-2a, Ropeginterferon alfa-2b, Interferon alfacon-1, Peginterferon beta-1a, Interferon beta-1a, Sargramostim, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/13006/', '[1] "Product Information. Proleukin (aldesleukin)." Chiron Therapeutics, Emeryville, CA.[2] "Product Information. Ultravist (iopromide)." Berlex, Richmond, CA.', 1767369485);
