INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (13276, 'Aldesleukin', 'Tuberculin purified protein derivative', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Interferon alfa-n1, Oprelvekin, Sipuleucel-T, Elapegademase, Interferon beta-1b, Peginterferon alfa-2a, Ropeginterferon alfa-2b, Interferon alfacon-1, Peginterferon beta-1a, Interferon beta-1a, Sargramostim, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/13280/', '[1] "Product Information. Spherulin (coccidioidin skin test)." ALK Laboratories Inc, Wallingford, CT.[2] "Product Information. Tuberculin PPD (Mantoux) (tuberculin purified protein derivative)." Connaught Laboratories Ltd, Toronto, ON.[3] "Product Information. Candin (candida albicans extract)." Nielsen Biosciences Inc,