INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (13764, 'Fingolimod', 'Alemtuzumab', 'Major', 'Coadministration of fingolimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of infections. Fingolimod causes reversible sequestration of lymphocytes in lymphoid tissues.', NULL, 'The safety and efficacy of fingolimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated.', NULL, 'The safety and efficacy of fingolimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated. Caution is advised during coadministration. A complete blood count is recommended prior to starting fingolimod if a recent one (i.e., within last 6 months) is not available.', 'Synergism', 'Pegcetacoplan, Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Emapalumab, Ravulizumab, Fingolimod, Eculizumab, Sutimlimab', 'Alemtuzumab, Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Emapalumab, Ravulizumab, Voclosporin, Pegcetacoplan, Eculizumab, Sutimlimab', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/13768/', '[1] "Product Information. Gilenya (fingolimod)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (13765, 'Floxuridine', 'Alemtuzumab', 'Moderate', 'The use of alemtuzumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Alemtuzumab alone may cause severe and prolonged myelosuppression, lymphopenia, and rarely, fatal bone marrow aplasia/hypoplasia, autoimmune idiopathic thrombocytopenia, and autoimmune hemolytic anemia. Serious, sometimes fatal opportunistic infections have been reported, and the risk may theoretically increase when coadministered with other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised if alemtuzumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, 'Caution is advised if alemtuzumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. The manufacturer recommends that single doses of alemtuzumab not exceed 30 mg and cumulative weekly doses not exceed 90 mg, since higher dosages are associated with an increased incidence of pancytopenia. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy.', 'Synergism', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Tacrolimus, Abatacept, Apremilast, Inebilizumab, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/13769/', '[1] "Product Information. Campath (alemtuzumab)" Berlex, Richmond, CA.[2] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.', 1767369485);
