INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (13977, 'Foscarnet', 'Alendronic acid', 'Moderate', 'Coadministration with other drugs that can reduce serum calcium may potentiate the risk of hypocalcemia associated with foscarnet. Foscarnet chelates with divalent metal ions and causes a dose-related reduction in ionized serum calcium.', NULL, 'Close monitoring is recommended during concomitant use of foscarnet with other drugs that are known to affect calcium concentrations.', NULL, 'Close monitoring is recommended during concomitant use of foscarnet with other drugs that are known to affect calcium concentrations. Serum creatinine and electrolyte levels should be assessed prior to initiating therapy and frequently during therapy. Patients should be advised to promptly notify their physician if they experience potential symptoms of hypocalcemia such as perioral tingling, numbness in extremities, and paresthesias.', 'Distribution', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Cholecalciferol, Vosoritide, Burosumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/13981/', '[1] "Product Information. Foscavir (foscarnet)." Astra USA, Westborough, MA.[2] "Foscarnet: a review." Drugs 41 (1991): 106-29[3] Youle M, Clarbour J, Gazzard B, Chanas A "Severe hypocalcemia in AIDS patients treated with foscarnet and pentamidine." Lancet 1 (1988): 1455-6[4] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (13978, 'Furosemide', 'Alendronic acid', 'Moderate', 'Coadministration of bisphosphonates and loop diuretics may potentiate the risk of hypocalcemia. Bisphosphonates alone often cause mild, asymptomatic hypocalcemia via inhibitive effects on bone resorption and possibly chelation of blood calcium. These effects may be additive with those of loop diuretics, which tend to increase the urinary excretion of calcium.', NULL, 'Caution is advised if bisphosphonates are coadministered with loop diuretics.', NULL, 'Caution is advised if bisphosphonates are coadministered with loop diuretics. Renal function as well as serum calcium levels should be monitored during and after treatment, since the effects of bisphosphonates can continue for many weeks after discontinuation. Patients and clinicians should be alert to signs and symptoms of clinical hypocalcemia such as muscle spasm, carpopedal spasm, laryngeal spasm, tetany, convulsions, mental changes (irritability, depression, psychosis), increased intracranial pressure, papilledema, QT interval prolongation, and cardiac arrhythmias.', 'Synergism', 'Romosozumab, Cholecalciferol, Denosumab, Burosumab', 'Trichlormethiazide, Bendroflumethiazide, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/13982/', '[1] "Product Information. Zometa (zoledronic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Skelid (tilundronate)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767369485);
