INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327716, 'Brigatinib', 'Acebutolol', 'Moderate', 'Coadministration of brigatinib with other agents that can also slow the heart rate may potentiate the risk of severe bradycardia.', 'DDInter', 1767317474, 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents. Heart rate and blood pressure should be monitored frequently, and patients should be advised to seek medical attention if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327717, 'Brimonidine (ophthalmic)', 'Acebutolol', 'Moderate', 'Topically administered alpha-2 adrenergic receptor agonists such as apraclonidine and brimonidine are systemically absorbed, with the potential for producing rare but clinically significant systemic effects such as hypotension and bradycardia. The possibility for an additive or potentiating effect on blood pressure and heart rate should be considered when used with other medications that affect these parameters, such as ophthalmic and systemic beta blockers, vasodilators, cardiac glycosides, and antihypertensive agents.', 'DDInter', 1767317474, 'Blood pressure and pulse rate should be monitored regularly when topical alpha-2 adrenergic receptor agonists are prescribed in combination with cardiovascular drugs. Patients should be advised to notify their physician if they experience slow pulse, irregular heartbeat, dizziness, lightheadedness, or syncope.', 'Synergism', 'Blood pressure and pulse rate should be monitored regularly when topical alpha-2 adrenergic receptor agonists are prescribed in combination with cardiovascular drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327718, 'Brimonidine (topical)', 'Acebutolol', 'Moderate', 'Topically administered alpha-2 adrenergic receptor agonists such as apraclonidine and brimonidine are systemically absorbed, with the potential for producing rare but clinically significant systemic effects such as hypotension and bradycardia. The possibility for an additive or potentiating effect on blood pressure and heart rate should be considered when used with other medications that affect these parameters, such as ophthalmic and systemic beta blockers, vasodilators, cardiac glycosides, and antihypertensive agents.', 'DDInter', 1767317474, 'Blood pressure and pulse rate should be monitored regularly when topical alpha-2 adrenergic receptor agonists are prescribed in combination with cardiovascular drugs. Patients should be advised to notify their physician if they experience slow pulse, irregular heartbeat, dizziness, lightheadedness, or syncope.', 'Synergism', 'Blood pressure and pulse rate should be monitored regularly when topical alpha-2 adrenergic receptor agonists are prescribed in combination with cardiovascular drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
