INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327722, 'Bumetanide', 'Acebutolol', 'Moderate', 'Although they are often combined in clinical practice, diuretics and beta-blockers may increase the risk of hyperglycemia and hypertriglyceridemia in some patients, especially in patients with diabetes or latent diabetes. In addition, the risk of QT interval prolongation and arrhythmias (e.g. torsades de pointes) due to sotalol may be increased by potassium-depleting diuretics.', 'DDInter', 1767317474, 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration. Patients should be advised to seek medical assistance if they experience dizziness, weakness, fainting, fast or irregular heartbeats, or loss of blood glucose control.', 'Synergism', 'Monitoring of serum potassium levels, blood pressure, and blood glucose is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327723, 'Bupivacaine', 'Acebutolol', 'Moderate', 'Beta-blockers may increase the risk of bupivacaine-induced side effects. The proposed mechanism is increased bupivacaine levels due to hepatic enzyme inhibition (propranolol) and/or additive negative inotropic effects on the heart.', 'DDInter', 1767317474, 'Caution is recommended if multiple doses of bupivacaine are administered in the presence of a beta-blocker. Monitoring for drowsiness, mental status changes, convulsions, ECG changes, and hypotension is advisable during concurrent therapy.', 'Synergism', 'Caution is recommended if multiple doses of bupivacaine are administered in the presence of a beta-blocker.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327724, 'Buprenorphine', 'Acebutolol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317474, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
