INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327742, 'Ceritinib', 'Acebutolol', 'Major', 'Coadministration of ceritinib with other drugs that can slow the heart rate such as beta-blockers, calcium channel blockers, digitalis, and clonidine may increase the risk of bradycardia, which can lead to heart block and QT prolongation.', 'DDInter', 1767317474, 'Concomitant use of ceritinib with other agents known to cause bradycardia should be avoided if possible. All patients treated with ceritinib should have their heart rate and blood pressure monitored regularly. Consult the product labeling for dosage adjustment and management recommendations if symptomatic or life-threatening bradycardia occurs. Patients should be advised to notify their doctor if they experience irregular heartbeat, slow pulse, dizziness, or syncope.', 'Synergism', 'Concomitant use of ceritinib with other agents known to cause bradycardia should be avoided if possible.', NULL, NULL, 'Major', 'pharmacodynamic');
