INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7061, 'Acitretin', 'Methoxsalen', 'Moderate', 'Concomitant use of methoxsalen with other known photosensitizing agents may increase the risk of photosensitivity reactions. These agents include anthralin, coal tar or coal tar derivatives, griseofulvin, hypericin extracts (e.g., St John''s Wort), fluoroquinolones, phenothiazines, retinoids, halogenated salicylanilides (bacteriostatic soaps), sulfonamides, sulfonylureas, tetracyclines, thiazides, and certain organic staining dyes such as methylene blue, toluidine blue, rose bengal, and methyl orange.', NULL, 'Caution is advised and pharmacologic response to methoxsalen therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', NULL, 'Caution is advised and pharmacologic response to methoxsalen therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid sun exposure, even through window glass or cloud cover, for at least 8 hours after methoxsalen ingestion and during the 24 hours following photochemotherapy or photopheresis treatment.', 'Synergism', 'Methoxsalen', 'Acitretin, Trioxsalen, Tazarotene, Trioxsalen, Calcitriol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7065/', '[1] "Product Information. Uvadex (methoxsalen)." Therakos Inc, Exton, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Multum Information Services, Inc. Expert Review Panel"[4] "Product Information. Oxsoralen (methoxsalen)." ICN Pharmaceuticals Inc, Costa Mesa, CA.[5] Royal Australian College of General Practicioners (RACGP), the Pharmaceutical Society of Australia (PSA), the Australasian Society of Clinical and Experimental Pharmacologists and Toxicologists (ASCEPT) "Australian Medicines Handbook. Available from: URL: https://www.amh.net.au/"[6] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[7] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7062, 'Acitretin', 'Methyl aminolevulinate (topical)', 'Moderate', 'Concomitant use of aminolevulinate topical preparations with other known photosensitizing agents may enhance the phototoxic reaction to photodynamic therapy. Medicinal products with known phototoxic or photoallergic potential include fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, and hypericin extracts (e.g., St John''s Wort).', NULL, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', NULL, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid exposure of treated areas to sunlight or bright indoor lights (e.g., examination lamps, operating room lamps, tanning beds, lights at close proximity) during the period between application of aminolevulinic acid or methyl aminolevulinate and photoactivation, and for 48 hours post-illumination. Concomitant use with other topical medicinal products should be avoided.', 'Synergism', 'Etretinate', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7066/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Metvixia (methyl aminolevulinate topical)." Galderma Laboratories Inc, Cranbury, NJ.[4] "Product Information. Levulan Kerastick (aminolevulinic acid)" Berlex, Richmond, CA.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7063, 'Acitretin', 'Minocycline', 'Major', 'Concomitant use of vitamin A or related compounds (i.e., retinoids) with tetracyclines may increase the risk of pseudotumor cerebri, also known as benign intracranial hypertension. These agents individually have been associated with the development of pseudotumor cerebri and may have additive effects during coadministration. The interaction has been suspected in reported cases involving the use of isotretinoin or vitamin A in combination with a tetracycline.', NULL, 'Coadministration of most retinoids with tetracyclines is considered contraindicated.', NULL, 'Coadministration of most retinoids with tetracyclines is considered contraindicated. Patients treated with a retinoid or a tetracycline should be advised to discontinue the medication and notify their physician immediately if they develop early symptoms of pseudotumor cerebri such as headache, nausea, vomiting, visual disturbances, vision loss, and papilledema. Although intracranial hypertension typically resolves after discontinuation of treatment, the possibility for permanent visual loss exists. Therefore, prompt ophthalmologic evaluation should be performed if visual disturbances occur. Since intracranial pressure may remain elevated for weeks after drug cessation, patients should be monitored until they stabilize.', 'Synergism', 'Trioxsalen', 'Clindamycin, Chloramphenicol, Fluorometholone, Erythromycin, Azelaic acid, Adapalene, Tigecycline, Amphotericin B, Miconazole, Hydrocortisone, Metronidazole, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7067/', '[1] Gardner K, Cox T, Digre KB "Idiopathic intracranial hypertension associated with tetracycline use in fraternal twins: case reports and review." Neurology 45 (1995): 6-10[2] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[3] Walters BN, Gubbay SS "Tetracycline and benign intracranial hypertension: report of five cases." Br Med J 282 (1981): 19-20[4] Moskowitz Y, Leibowitz E, Ronen M, Aviel E "Pseudotumor cerebri induced by vitamin A combined with minocycline." Ann Ophthalmol 25 (1993): 306-8[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Weese-Mayer DE, Yang RJ, Mayer JR, Zaparackas Z "Minocycline and Pseudotumor cerebri: The well-known but well-kept secret." Pediatrics 108 (2001): 519-20[7] Donnet A, Dufour H, Graziani N, Grisoli F "Minocycline and benign intracranial hypertension." Biomed Pharmacother 46 (1992): 171-2[8] Yokokura M, Hatake K, Komatsu N, Kajitani H, Miura Y "Toxicity of tretinoin in acute promyelocytic leukaemia." Lancet 343 (1994): 361-2[9] Cerner Multum, Inc. "Australian Product Information." O 0[10] Chan AY, Liu DT, Friedman DI, Gordon LK, Egan RA "Doxycycline and intracranial hypertension." Neurology 64 (2005): 765-6[11] Chiu AM, Chuenkongkaew WL, Cornblath WT, Trobe JD, Digre KB, Dotan SA, Musson KH, Eggenberger ER "Minocycline treatment and pseudotumor cerebri syndrome." Am J Ophthalmol 126 (1998): 116-21[12] Lewis PA, Kearney PJ "Pseudotumor cerebri induced by minocycline treatment for acne vulgaris." Acta Derm Venereol 77 (1997): 83[13] Lee AG "Pseudotumor cerebri after treatment with tetracycline and isotretinoin for acne." Cutis 55 (1995): 165-8[14] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.[15] "Product Information. Vesanoid (tretinoin)." Roche Laboratories, Nutley, NJ.[16] Schmitt K, Schwarz R, Tulzer G, Krieger O, Zoubek A, Gadner H "Toxicity of tretinoin in acute promyelocytic leukaemia." Lancet 343 (1994): 361[17] Minutello JS, Dimayuga RG, Carter J "Pseudotumor cerebri, a rare adverse reaction