INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7149, 'Aclidinium', 'Physostigmine (ophthalmic)', 'Moderate', 'Theoretically, anticholinergic agents and other agents with significant anticholinergic activity (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, class IA antiarrhythmics especially disopyramide) may antagonize the effects of topically administered cholinergic agents such as acetylcholine, carbachol, demecarium, echothiophate, isoflurophate, physostigmine, and pilocarpine. The proposed mechanism involves opposing pharmacodynamic action on muscarinic receptor sites in ocular tissue. This interaction is sometimes desirable and is the basis for using atropine in the treatment of excessive muscarinic side effects and cholinergic crisis induced by cholinergic overdose.', NULL, 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', NULL, 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', 'Antagonism', 'Ciclesonide, Mometasone furoate, Budesonide, Flunisolide, Fluticasone, Cromoglicic acid, Betamethasone, Nedocromil, Triamcinolone, Beclomethasone dipropionate, Mometasone furoate, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7153/', '[1] "Product Information. Pilopine HS (pilocarpine ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Multum Information Services, Inc. Expert Review Panel"', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7150, 'Aclidinium', 'Pilocarpine', 'Moderate', 'Anticholinergic agents and other agents with significant anticholinergic activity (e.g., clozapine, class IA antiarrhythmics especially disopyramide) may antagonize the effects of direct-acting cholinergic agents such as bethanechol, carbachol, cevimeline, and pilocarpine. This interaction is sometimes desirable and is the basis for using atropine in the treatment of excessive muscarinic side effects and cholinergic crisis induced by cholinergic overdose. Conversely, cholinergic agents may also counteract the pharmacologic effects of anticholinergic agents and other agents that rely partially on their anticholinergic activity for therapeutic effects (e.g., some antiparkinsonian and antiemetic/antivertigo agents). The mechanism of interaction involves opposing pharmacodynamic action on muscarinic receptor sites.', NULL, 'It may be appropriate to avoid concomitant use of anticholinergic agents and cholinergic agents, depending on the needs of the patient.', NULL, 'It may be appropriate to avoid concomitant use of anticholinergic agents and cholinergic agents, depending on the needs of the patient. If concurrent use is necessary, the patient should be monitored for reduced pharmacologic effects of both drugs.', 'Antagonism', 'Ambenonium, Neostigmine, Pyridostigmine, Latanoprost, Apraclonidine, Methazolamide, Neostigmine, Clonidine, Physostigmine, Betaxolol, Diclofenamide, More', 'Ciclesonide, Mometasone furoate, Flunisolide, Cromoglicic acid, Betamethasone, Nedocromil, Beclomethasone dipropionate, Mometasone furoate, Terbutaline, Mometasone, Olodaterol, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'ht