INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7270, 'Formoterol', 'Droxidopa', 'Moderate', 'Agents that increase blood pressure such as vasopressors, triptans, decongestants, beta-2 agonists, and amphetamines may potentiate the risk of supine hypertension associated with the use of droxidopa in the treatment of neurogenic orthostatic hypotension.', NULL, 'Supine blood pressure should be monitored prior to and during droxidopa treatment, and more frequently when increasing dosage.', NULL, 'Supine blood pressure should be monitored prior to and during droxidopa treatment, and more frequently when increasing dosage. Patients should elevate the head of the bed when resting or sleeping, and have blood pressure also measured in this position. Dosage reduction or discontinuation of droxidopa is recommended if supine hypertension cannot be managed by elevation of the head of the bed. Supine hypertension that is not well managed may increase the risk of cardiovascular events, particularly stroke.', 'Synergism', 'Mometasone furoate, Umeclidinium, Budesonide, Glycopyrronium, Aclidinium, Mometasone, Fluticasone, Cromoglicic acid, Ipratropium, Olodaterol, Tiotropium, Beclomethasone dipropionate', 'Amrinone, Angiotensin II, Milrinone, Arbutamine, Fenoldopam', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7274/', '[1] "Product Information. Northera (droxidopa)." Chelsea Therapeutics Inc, Charlotte, NC.', 1767369485);
