INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327896, 'Acebutolol', 'Levodopa', 'Moderate', 'The hypotensive effects of levodopa and antihypertensive agents may be additive. Postural hypotension may occur.', 'DDInter', 1767317474, 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy. Dose adjustments of the antihypertensive agent may be required. Patients should be advised to notify their physician if they experience dizziness or syncope.', 'Synergism', 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327897, 'Acebutolol', 'Levothyroxine', 'Minor', 'Thyroid hormone therapy may reverse decreased hepatic blood flow associated with hypothyroidism. Increased hepatic metabolism and decreased serum levels of some beta-blockers may result. Data are available for propranolol only. No special precautions are necessary. When hypothyroidism is converted to a euthyroid state, a decrease in beta-blocking effectiveness is possible.', 'DDInter', 1767317474, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327898, 'Acebutolol', 'Licorice', 'Moderate', 'Licorice use has been associated with hypertension and may antagonize the effects of antihypertensive agents. Glycyrrhizic acid, a component of licorice, is hydrolyzed in the intestine to a metabolite (glycyrrhetinic acid) that causes mineralocorticoid and renin-suppressing effects.', 'DDInter', 1767317474, 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products. Even relatively moderate doses of licorice may be problematic in susceptible patients when ingested regularly for prolonged periods.', 'Antagonism', 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327899, 'Acebutolol', 'Linezolid', 'Moderate', 'Sympathetic ganglion-blocking or catecholamine-depleting agents such as guanethidine, reserpine, and monoamine oxidase (MAO) inhibitors may potentiate the pharmacologic effects of beta-blockers, which are thought to competitively antagonize catecholamines at cardiac and other peripheral adrenergic neurons. Combining these medications may increase the risk of hypotension, orthostasis, bradycardia, and heart failure due to excessive reduction of sympathetic activity.', 'DDInter', 1767317474, 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents. Patients should contact their doctor if they experience dizziness, lightheadedness, syncope, bradycardia, shortness of breath, difficulty breathing, edema, and/or chest pain.', 'Synergism', 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
