INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327960, 'Octreotide', 'Acebutolol', 'Moderate', 'Somatostatin analogs (e.g., octreotide, lanreotide) can decrease heart rate and may potentiate the bradycardic effects of drugs like beta-blockers, calcium channel blockers, digitalis glycosides, and certain antiarrhythmic agents (e.g., class I antiarrhythmics, adenosine, amiodarone).', 'DDInter', 1767317474, 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers. Heart rate and blood pressure should be monitored.', 'Synergism', 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1327961, 'Olanzapine', 'Acebutolol', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317474, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
