INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328000, 'Propafenone', 'Acebutolol', 'Moderate', 'Propafenone may significantly increase serum levels and effects of some oral beta-blockers. The proposed mechanism is inhibition of CYP450 2D6 first-pass metabolism and decreased hepatic clearance. Data are available for metoprolol and propranolol only; however, other hepatically metabolized oral beta-blockers may also be affected. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact. In addition, negative inotropic effects may be potentiated.', 'DDInter', 1767317474, 'Patients receiving this combination should be monitored for hypotension, heart failure, bradycardia, arrhythmias, and mental status changes. Beta-blocker dosage may be decreased if necessary.', 'Metabolism', 'Patients receiving this combination should be monitored for hypotension, heart failure, bradycardia, arrhythmias, and mental status changes.', NULL, NULL, 'Moderate', 'pharmacodynamic');
