INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328004, 'Propylthiouracil', 'Acebutolol', 'Moderate', 'The clearance of some beta-blockers with high extraction ratios may be reduced when a euthyroid state is achieved after the addition of antithyroid agents. Sotalol, nadolol, carteolol, and atenolol, which are primarily eliminated by the kidney, do not appear to be affected by changes in thyroid function.', 'DDInter', 1767317474, 'The patient should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when the antithyroid agent dosage is added, discontinued, or changed. Beta blocker dose reductions may be required.', 'Others', 'The patient should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when the antithyroid agent dosage is added, discontinued, or changed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328005, 'Protriptyline', 'Acebutolol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317474, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328006, 'Pseudoephedrine', 'Acebutolol', 'Moderate', 'Sympathomimetic agents, such as pseudoephedrine, may antagonize the effects of antihypertensives that interfere with sympathetic activity, such as alpha- and beta-adrenergic blocking agents. In contrast, alpha- and beta-blockers may diminish the vasoconstrictive effects of sympathomimetics. The mechanism of this interaction has not been reported, but may involve competitive inhibition at the adrenergic receptor. Severe hypertension may occur in patients receiving beta-blockers.', 'DDInter', 1767317474, 'Caution and close monitoring of blood pressure is recommended if pseudoephedrine is administered with these antihypertensives. Discontinuation of the sympathomimetic may be necessary if hypertension develops.', 'Antagonism', 'Caution and close monitoring of blood pressure is recommended if pseudoephedrine is administered with these antihypertensives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
