INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328165, 'Deferasirox', 'Acetaminophen', 'Moderate', 'Coadministration with deferasirox may increase the plasma concentrations of drugs that are substrates of the CYP450 1A2 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 1A2 activity by deferasirox.', 'DDInter', 1767317474, 'Caution is advised if deferasirox must be used concurrently with medications that undergo metabolism by CYP450 1A2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever deferasirox is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if deferasirox must be used concurrently with medications that undergo metabolism by CYP450 1A2, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
