INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328169, 'Diflunisal', 'Acetaminophen', 'Minor', 'Acetaminophen serum concentrations may be elevated by as much as 50% during concomitant therapy with diflunisal in normal subjects. The clinical significance of this finding is unknown. Because elevated acetaminophen levels are associated with hepatotoxicity, the manufacturer recommends caution when this combination is used.', 'DDInter', 1767317474, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328170, 'Echinacea', 'Acetaminophen', 'Moderate', 'Coadministration with echinacea may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 1A2. The proposed mechanism is decreased clearance due to echinacea-mediated inhibition of CYP450 1A2.', 'DDInter', 1767317474, 'Caution is advised if echinacea is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever echinacea is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if echinacea is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328171, 'Efavirenz', 'Acetaminophen', 'Moderate', 'Coadministration of efavirenz with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Efavirenz has been associated with hepatotoxicity during postmarketing use.', 'DDInter', 1767317474, 'The risk of hepatic injury should be considered when efavirenz is used in combination with other agents that are potentially hepatotoxic (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; other HIV reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of liver function tests should occur before and during treatment, especially in patients with underlying hepatic disease (including hepatitis B or C coinfection) or marked transaminase elevations.', 'Synergism', 'The risk of hepatic injury should be considered when efavirenz is used in combination with other agents that are potentially hepatotoxic (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328172, 'Epirubicin', 'Acetaminophen', 'Moderate', 'The concomitant or sequential use of epirubicin with agents known to induce hepatotoxicity may potentiate the risk of liver injury. Epirubicin undergoes elimination through the hepatobiliary system.', 'DDInter', 1767317474, 'Caution and clinical and laboratory monitoring for signs of liver injury are advisable before, during, or after use of epirubicin with other agents that are potentially hepatotoxic.', 'Synergism', 'Caution and clinical and laboratory monitoring for signs of liver injury are advisable before, during, or after use of epirubicin with other agents that are potentially hepatotoxic.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328173, 'Ethanol', 'Acetaminophen', 'Major', 'Chronic, excessive consumption of alcohol may increase the risk of acetaminophen-induced hepatotoxicity, which has included rare cases of fatal hepatitis and frank hepatic failure requiring liver transplantation. The proposed mechanism is induction of hepatic microsomal enzymes during chronic alcohol use, which may result in accelerated metabolism of acetaminophen and increased production of potentially hepatotoxic metabolites.', 'DDInter', 1767317474, 'In general, chronic alcoholics should avoid regular or excessive use of acetaminophen. Alternative analgesic/antipyretic therapy may be appropriate in patients who consume three or more alcoholic drinks per day. However, if acetaminophen is used, these patients should be cautioned not to exceed the recommended dosage (maximum 4 g/day in adults and children 12 years of age or older).', 'Metabolism', 'In general, chronic alcoholics should avoid regular or excessive use of acetaminophen.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328174, 'Ethinylestradiol', 'Acetaminophen', 'Minor', 'Oral contraceptives may decrease or delay the effects of acetaminophen by increasing glucuronidation. With the usual therapeutic dosage, concurrent administration probably results in no significant additional risk.', 'DDInter', 1767317474, '-', 'Antagonism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328175, 'Ethotoin', 'Acetaminophen', 'Moderate', 'Hydantoins may increase the potential hepatotoxicity of acetaminophen and decrease its pharmacologic effects. The mechanism may be related to induction of acetaminophen metabolism with consequent increase in hepatotoxic metabolites. This interaction is of greatest concern in cases of acetaminophen overdose.', 'DDInter', 1767317474, 'Monitoring of liver function is recommended. Patients should be advised to avoid large doses or prolonged use of acetaminophen.', 'Metabolism', 'Monitoring of liver function is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328176, 'Fenofibrate', 'Acetaminophen', 'Moderate', 'Coadministration with fenofibrate may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of CYP450 2C19, 2A6, and 2C9 isoenzymes. The proposed mechanism, based on in vitro data, is decreased clearance due to fenofibrate-mediated inhibition of CYP450 2C19, 2A6, and 2C9.', 'DDInter', 1767317474, 'Until more information is available, caution is advised if fenofibrate is used concomitantly with drugs that are substrates of CYP450 2C19, 2A6, or 2C9 isoenzymes, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever fenofibrate is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Until more information is available, caution is advised if fenofibrate is used concomitantly with drugs that are substrates of CYP450 2C19, 2A6, or 2C9 isoenzymes, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328177, 'Fenoldopam', 'Acetaminophen', 'Minor', 'The use of acetaminophen has been associated with a 32% increase in orally-administered fenoldopam plasma concentrations and a 50% increase in the area under the plasma concentration versus time curve for fenoldopam. These data suggest that acetaminophen may significantly increase exposure to fenoldopam and increase its hypotensive effect. Data are available for orally administered fenoldopam only. Since fenoldopam is available as an intravenously administered drug for emergent hypertension, its dose can be rapidly titrated in cases of hypotension.', 'DDInter', 1767317474, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
