INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328183, 'Hyoscyamine', 'Acetaminophen', 'Minor', 'Anticholinergic agents may delay and/or decrease the gastrointestinal absorption of acetaminophen by reducing gastric motility and delaying gastric emptying. However, the clinical relevance is probably minimal.', 'DDInter', 1767317474, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328184, 'Idelalisib', 'Acetaminophen', 'Moderate', 'Coadministration of idelalisib with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. The use of idelalisib has been associated with elevations in alanine aminotransferase (ALT) and aspartate aminotransferase (AST) greater than 5 times the upper limit of normal.', 'DDInter', 1767317474, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with idelalisib should have serum ALT, AST, and bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary.', 'Synergism', 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328185, 'Imatinib', 'Acetaminophen', 'Moderate', 'In vitro inhibition data suggest that coadministration with imatinib may increase the plasma concentrations of acetaminophen. The proposed mechanism is imatinib inhibition of acetaminophen clearance via O-glucuronidation. However, no specific pharmacokinetic studies in humans have been performed. Pharmacodynamically, the potential may exist for additive adverse effects on the liver, since both agents individually are associated with hepatotoxicity.', 'DDInter', 1767317474, 'Caution is advised if imatinib is used in combination with acetaminophen, particularly at higher dosages of the latter. All patients treated with imatinib should have liver function tests (transaminases, bilirubin, and alkaline phosphatase) prior to initiation of therapy and monthly thereafter or as clinically indicated. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. In addition, they should not take over-the-counter medications such as analgesics containing acetaminophen, flu preparations, or herbal products without first talking to their healthcare provider.', 'Synergism', 'Caution is advised if imatinib is used in combination with acetaminophen, particularly at higher dosages of the latter.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328186, 'Interferon beta-1a', 'Acetaminophen', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', 'DDInter', 1767317474, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328187, 'Interferon beta-1b', 'Acetaminophen', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', 'DDInter', 1767317474, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328188, 'Isoniazid', 'Acetaminophen', 'Moderate', 'Some reports have suggested that isoniazid may increase the potential hepatotoxicity of acetaminophen. The mechanism may be related to induction of CYP450 2E1 metabolism of acetaminophen to toxic metabolites during concurrent administration, or transient increased metabolism of acetaminophen after discontinuation of isoniazid. Both isoniazid and acetaminophen have individually been associated with hepatotoxicity.', 'DDInter', 1767317474, 'Until more information is available, concurrent acetaminophen use should be limited. Close attention should be paid to clinical and laboratory evidence of hepatotoxicity. Both drugs should be discontinued if evidence of hepatoxicity is observed. Aspirin or nonsteroidal inflammatory agents may be safer alternatives.', 'Metabolism', 'Until more information is available, concurrent acetaminophen use should be limited.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328189, 'Acetaminophen', 'Leflunomide', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', 'DDInter', 1767317474, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), and vice versa. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times ULN) should not receive leflunomide or teriflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years. Follow-up monitoring should be conducted at least weekly until the ALT value is within normal range, and washout procedures repeated as necessary. All patients treated with leflunomide or teriflunomide should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328190, 'Acetaminophen', 'Lidocaine (ophthalmic)', 'Moderate', 'Some topical anesthetics can be systemically absorbed and cause methemoglobinemia, particularly when applied to mucous membranes. Coadministration with other oxidizing agents that can also induce methemoglobinemia such as injectable local anesthetics, antimalarials (e.g., chloroquine, primaquine, quinine, tafenoquine), nitrates and nitrites, sulfonamides, acetaminophen, aminosalicylic acid, dapsone, dimethyl sulfoxide, flutamide, metoclopramide, nitrofurantoin, phenazopyridine, phenobarbital, phenytoin, and rasburicase may increase the risk. Additional risk factors include very young age (e.g., infants less than 6 months); application to inflamed/abraded areas or broken skin; anemia; cardiac or pulmonary disease; peripheral vascular disease; liver cirrhosis; shock; sepsis; acidosis; and genetic predisposition (e.g., NADH cytochrome-b5 reductase deficiency; glucose-6-phosphate dehydrogenase (G6PD) deficiency; hemoglobin M).', 'DDInter', 1767317474, 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents. Clinicians should be aware of the potential for methemoglobinemia, particularly when topical anesthetics are applied to mucous membranes or given via the oropharyngeal route. Patients or their caregivers should be advised to seek medical attention if they notice signs and symptoms of methemoglobinemia such as slate-grey cyanosis in buccal mucous membranes, lips, and nail beds; nausea; headache; dizziness; lightheadedness; lethargy; fatigue; dyspnea; tachypnea; tachycardia; palpitation; anxiety; and confusion.', 'Synergism', 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328191, 'Acetaminophen', 'Lidocaine (topical)', 'Moderate', 'Some topical anesthetics can be systemically absorbed and cause methemoglobinemia, particularly when applied to mucous membranes. Coadministration with other oxidizing agents that can also induce methemoglobinemia such as injectable local anesthetics, antimalarials (e.g., chloroquine, primaquine, quinine, tafenoquine), nitrates and nitrites, sulfonamides, acetaminophen, aminosalicylic acid, dapsone, dimethyl sulfoxide, flutamide, metoclopramide, nitrofurantoin, phenazopyridine, phenobarbital, phenytoin, and rasburicase may increase the risk. Additional risk factors include very young age (e.g., infants less than 6 months); application to inflamed/abraded areas or broken skin; anemia; cardiac or pulmonary disease; peripheral vascular disease; liver cirrhosis; shock; sepsis; acidosis; and genetic predisposition (e.g., NADH cytochrome-b5 reductase deficiency; glucose-6-phosphate dehydrogenase (G6PD) deficiency; hemoglobin M).', 'DDInter', 1767317474, 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents. Clinicians should be aware of the potential for methemoglobinemia, particularly when topical anesthetics are applied to mucous membranes or given via the oropharyngeal route. Patients or their caregivers should be advised to seek medical attention if they notice signs and symptoms of methemoglobinemia such as slate-grey cyanosis in buccal mucous membranes, lips, and nail beds; nausea; headache; dizziness; lightheadedness; lethargy; fatigue; dyspnea; tachypnea; tachycardia; palpitation; anxiety; and confusion.', 'Synergism', 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328192, 'Acetaminophen', 'Liraglutide', 'Minor', 'Liraglutide delays gastric emptying, which may impact the absorption of concomitantly administered oral medications.', 'DDInter', 1767317474, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328193, 'Acetaminophen', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Lomitapide can cause elevations in serum transaminases and hepatic steatosis. Clinical data suggest that hepatic fat accumulation is reversible after stopping treatment with lomitapide, although the long-term consequences are unknown.', 'DDInter', 1767317474, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
