INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328194, 'Acetaminophen', 'Lorlatinib', 'Moderate', 'Coadministration with lorlatinib may decrease the plasma concentrations of drugs that are substrates of the uridine diphosphate glucuronosyl transferase (UGT) enzymes. The proposed mechanism involves decreased metabolic clearance due to induction of UGT by lorlatinib.', 'DDInter', 1767317474, 'Caution is advised when lorlatinib is used concurrently with drugs that are known UGT substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lorlatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when lorlatinib is used concurrently with drugs that are known UGT substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
