INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328347, 'Cycloserine', 'Caffeine', 'Moderate', 'Coadministration with caffeine may potentiate the central nervous system adverse effects of cycloserine and its prodrug, terizidone. These effects may include insomnia, excitability, irritability, anxiety, tremor, psychoses, and convulsions.', 'DDInter', 1767317474, 'Caution is advised when cycloserine or terizidone is used with caffeine. Consumption of certain beverages or stimulants with very high caffeine levels should be avoided as a precautionary measure.', 'Synergism', 'Caution is advised when cycloserine or terizidone is used with caffeine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
