INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328439, 'Hyoscyamine', 'Dihydrocodeine', 'Moderate', 'Coadministration of opioids with anticholinergic agents may result in additive central nervous system (CNS), gastrointestinal, and genitourinary effects. The risk and/or severity of adverse effects such as sedation, dizziness, confusion, cognitive and psychomotor impairment, dry mouth, constipation, and urinary retention may increase. Severe constipation may lead to paralytic ileus in some cases.', 'DDInter', 1767317474, 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328440, 'Ifosfamide', 'Dihydrocodeine', 'Moderate', 'Centrally-acting agents such as antiemetics, sedatives, narcotics, or antihistamines may add to the neurotoxic effects of ifosfamide. Administration of ifosfamide can cause CNS toxicity, which necessitates careful monitoring of the patient. Neurologic manifestations include somnolence, confusion, hallucinations, blurred vision, psychotic behavior, extrapyramidal symptoms, urinary incontinence, seizures, and in some instances, coma. There have also been reports of peripheral neuropathy. Ifosfamide neurotoxicity may occur within a few hours to a few days after initial administration and typically resolves within 48 to 72 hours of ifosfamide discontinuation.', 'DDInter', 1767317474, 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide. If ifosfamide-induced encephalopathy occurs, both ifosfamide and non-essential concomitant CNS agents should be discontinued. Supportive therapy should be initiated and maintained until complete resolution of neurotoxicity.', 'Synergism', 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide.', NULL, NULL, 'Moderate', 'pharmacodynamic');
