INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15007, 'Fosfomycin', 'Balsalazide', 'Moderate', 'Balsalazide may be rendered less effective when administered with certain antibiotics. Balsalazide is acted upon in the lower gut by bacterial azoreduction to produce mesalamine, the therapeutically active ingredient. Although no studies have been done, it is theorized that oral antibiotics can interfere with this reduction to mesalamine.', NULL, 'If the patient must receive oral antibiotics, consideration should be given to using another 5-aminosalicylate such as mesalamine.', NULL, 'If the patient must receive oral antibiotics, consideration should be given to using another 5-aminosalicylate such as mesalamine.', 'Antagonism', 'Tinidazole, Linezolid, Oritavancin, Daptomycin, Methenamine, Spectinomycin, Lefamulin, Tedizolid, Dalbavancin', 'Budesonide, Hydrocortisone, Cromoglicic acid, Olsalazine, Prednisone, Sulfasalazine, Prednisolone, Betamethasone, Beclomethasone dipropionate, Mesalazine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15021/', '[1] "Product Information. Colazal (balsalazide)" Salix Pharmaceuticals, Palo Alto, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15008, 'Fosfomycin', 'Vibrio cholerae CVD 103-HgR strain live antigen (live)', 'Major', 'INTERVAL: The use of live, attenuated cholera vaccine with systemic antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Vibrio cholerae, thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', NULL, 'Live, attenuated cholera vaccine should not be administered during or for at least 14 days after treatment with systemic antibiotics.', NULL, 'Live, attenuated cholera vaccine should not be administered during or for at least 14 days after treatment with systemic antibiotics.', 'Antagonism', 'Lefamulin, Methenamine', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15022/', '[1] "Product Information. Vaxchora (cholera vaccine, live)." PaxVax, Miami, FL.[2] "Product Information. Vaxchora (cholera vaccine, live)." PaxVax, Miami, FL.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15009, 'Fosinopril', 'Alfuzosin', 'Moderate', 'Additive hypotensive effects may occur when ACE inhibitors are used in combination with alpha-blockers. In the presence of ACE inhibition, the risk and/or severity of first-dose effects associated with alpha-blockers such as postural hypotension and syncope may be increased.', NULL, 'Caution is advised during coadministration of these agents, particularly when initiating an alpha-blocker in the presence of an ACE inhibitor.', NULL, 'Caution is advised during coadministration of these agents, particularly when initiating an alpha-blocker in the presence of an ACE inhibitor. Small initial dosages of the alpha-blocker should be considered and gradually titrated to desired effect, while the ACE inhibitor dosage may also need to be reduced. Hemodynamic responses should be monitored, especially during the first few weeks of therapy. Taking the alpha-blocker at bedtime may minimize the occurrence of orthostatic effects. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', 'Dutasteride, Solifenacin', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15023/', '[1] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[2] Baba T, Tomiyama T, Takebe K "Enhancement by an ACE inhibitor of first-dose hypotension caused by an alpha-blocker." N Engl J Med 322 (1990): 1237[3] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[4] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[5] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15010, 'Fosinopril', 'Aliskiren', 'Major', 'In patients without diabetes, coadministration of aliskiren with ACE inhibitors or ARBs may also be associated with increased risk of symptomatic hypotension, hyperkalemia, and changes in renal function including acute renal failure. All drugs inhibiting the renin-angiotensin system (RAS) can have these effects, which may be additive during concomitant administration. The risk of symptomatic hypotension is increased in the presence of marked volume and/or salt depletion. In patients with type 2 diabetes and renal impairment, coadministration of aliskiren with ACE inhibitors or angiotensin receptor blockers (ARBs) has been associated with an increased risk of adverse events including renal complications, hyperkalemia, and hypotension.', NULL, 'The use of aliskiren with ACE inhibitors or ARBs is considered contraindicated in patients with diabetes and should be avoided in general, particularly in patients with moderate to severe renal impairment (i.', NULL, 'The use of aliskiren with ACE inhibitors or ARBs is considered contraindicated in patients with diabetes and should be avoided in general, particularly in patients with moderate to severe renal impairment (i.e., creatinine clearance (CrCl) < 60 mL/min). Prescribers should not initiate aliskiren in diabetic patients who are taking an ACE inhibitor or an ARB, and should stop any aliskiren-containing treatment if these patients are already receiving the combination. Alternative antihypertensive treatment should be considered as necessary. Most patients do not obtain any additional benefit from combination therapy relative to monotherapy; therefore, the potential risks should be thoroughly assessed when aliskiren is prescribed with ACE inhibitors or ARBs for the treatment of essential hypertension in patients without diabetes. Volume or salt depletion should be corrected prior to initiation of treatment. Routine monitoring of blood pressure, electrolytes, and renal function are recommended, particularly in the elderly or patients with worsening heart failure or a risk for dehydration. Potassium supplementation should generally be avoided unless it is closely monitored, and patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as weakness, listlessness, confusion, tingling of the extremities, and irregular heartbeat.', 'Synergism', 'Nebivolol', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15024/', '[1] MHRA. Medicines and Healthcare Regulatory Agency "Combination use of medicines from different classes of renin-angiotensin system blocking agents: risk of hyperkalaemia, hypotension, and impaired renal function--new warnings. Available from: URL: http://www.mhra.gov.uk/Safetyinformation/DrugSafetyUpdate/" ([2014 June]):[2] National Kidney Foundation "KDOQI Clinical Practice Guideline for Diabetes and CKD: 2012 update." Am J Kidney Dis 60 (2012): 850-86[3] Novartis International AG "Novartis announces termination of ALTITUDE study with Rasilez Tekturna in high-risk patients with diabetes and renal impairment. Available from: URL: http://cardiobrief.files.wordpress.com/2011/12/novartis-aliskiren-altitude-pr.pdf." ([2011 Dec 20]):[4] "Product Information. Tekturna (aliskiren)." Novartis Pharmaceuticals, East Hanover, NJ.[5] EMA. European Medicines Agency "PRAC recommends against combined use of medicines affecting the renin-angiotensin (RAS) system: recommendation will now be considered by CHMP for final opinion. Available from: URL: http://www.ema.europa.eu/docs/en_GB/document_library/Referrals_document/R" ([2014 Apr 11]):[6] Leclerc JM, Chief Scientific Officer and Senior Vice-President Clinical and Regulatory Affairs, Health Canada "Potential risks of cardiovascular and renal adverse events in patients with type 2 diabetes treated with aliskiren (RASILEZ) or aliskiren/hydrochlorothiazide (RASILEZ HCT). Available from: URL: http://www.hc-sc.gc.ca/dhp-mps/alt_formats/pdf/medeff/advisor" ([2012 Jan 18]):[7] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.[8] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[9] Tapaninen T, Neuvonen PJ, Niemi M "Grapefruit juice greatly reduces the plasma concentrations of the OATP2B1 and CYP3A4 substrate aliskiren." Clin Pharmacol Ther 88 (2010): 339-42[10] Vaidyanathan S, Jarugula V, Dieterich HA, Howard D, Dole WP "Clinical pharmacokinetics and pharmacodynamics of aliskiren." Clin Pharmacokinet 47 (2008): 515-31[11] Tapaninen T, Neuvonen PJ, Niemi M "Orange and apple juices greatly reduce the plasma concentrations of the OATP2B1 substrate aliskiren." Br J Clin Pharmacol 71 (2010): 718-26[12] "Product Information. Tekturna (aliskiren)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15011, 'Fosinopril', 'Allopurinol', 'Major', 'Coadministration of allopurinol with angiotensin converting enzyme (ACE) inhibitors has been associated with a risk of severe hypersensitivity reactions, neutropenia, agranulocytosis, and serious infections. The mechanism of interaction is unknown, but impaired renal function may be a predisposing factor. Case reports, albeit rare, have mostly involved captopril. No pharmacokinetic interactions have been reported between allopurinol and ACE inhibitors.', NULL, 'Caution is advised if allopurinol is prescribed in combination with an ACE inhibitor, particularly in the elderly and patients with renal impairment.', NULL, 'Caution is advised if allopurinol is prescribed in combination with an ACE inhibitor, particularly in the elderly and patients with renal impairment. Periodic monitoring of white blood cell counts is recommended. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop dyspnea; throat constriction; swelling of the face, lips, or tongue; urticaria; rash; fever; arthralgia; or myalgia. Patients should also contact their physician if they notice signs of infection or experience fever, chills, sore throat, lethargy, body aches, or other flu-like symptoms.', 'Others', 'Lesinurad, Febuxostat, Pegloticase, Colchicine, Sulfinpyrazone, Rasburicase', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15025/', '[1] Pennell DJ, Nunan TO, O''Doherty MJ, Croft DN "Fatal Stevens-Johnson syndrome in a patient on captopril and allopurinol." Lancet 1 (1984): 463[2] Duchin KL, McKinstry DN, Cohen AI, Migdalof BH "Pharmacokinetics of captopril in healthy subjects and in patients with cardiovascular diseases." Clin Pharmacokinet 14 (1988): 241-59[3] "Product Information. Zyloprim capsules (allopurinol)." Glaxo Wellcome, Research Triangle Park, NC.[4] Samanta A, Burden AC "Fever, myalgia, and arthralgia in a patient on captopril and allopurinol." Lancet 1 (1984): 679[5] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[6] Ahmad S "Allopurinol and enalapril: drug induced anaphylactic coronary spasm and acute myocardial infarction." Chest 108 (1995): 586[7] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[8] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538[9] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15012, 'Fosinopril', 'Alogliptin', 'Moderate', 'Limited clinical data suggest that dipeptidyl peptidase IV (DPP IV) inhibitors, e.g., gliptins, have been associated with the development of angioedema, and coadministration with other drugs that are also known to cause angioedema, such as angiotensin converting enzyme (ACE) inhibitors may theoretically increase the risk.', NULL, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when inhibitors are prescribed with ACE inhibitors.', NULL, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when inhibitors are prescribed with ACE inhibitors. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing. Close monitoring for the development of hypoglycemia is recommended if ACE inhibitors are coadministered with inhibitors, particularly in patients with advanced age and/or renal impairment and during the first month of treatment. Dosage adjustment may be required if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their physician if it occurs.', 'Synergism', 'Felodipine, Amlodipine, Verapamil, Bisoprolol, Quinapril, Quinapril', 'Lixisenatide, Albiglutide, Guar gum, Acarbose, Pramlintide, Liraglutide, Troglitazone, Semaglutide, Dulaglutide, Miglitol', 'Moderate', NUL