INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15434, 'Furosemide', 'Amifostine', 'Moderate', 'Concomitant use with hypotensive agents may increase the risk and severity of hypotension associated with amifostine. Hypotension has been reported frequently in patients treated with amifostine. It is usually transient (lasting from 5 to 15 minutes) and closely associated with amifostine administration, but may persist or become evident hours after administration.', NULL, 'If medically feasible, antihypertensive medications should be interrupted 24 hours prior to amifostine administration.', NULL, 'If medically feasible, antihypertensive medications should be interrupted 24 hours prior to amifostine administration. Blood pressure should be closely monitored during and after treatment. In addition to hypotension, transient hypertension or exacerbation of preexisting hypertension may occur from intravenous hydration, discontinuation of antihypertensive medications, or other causes. Patients who continue their hypotensive medication(s) during amifostine therapy should be carefully monitored for the development of hypotension.', 'Synergism', 'Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Levoleucovorin, Arginine, Methionine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15449/', '[1] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[2] "Product Information. Ethyol (amifostine)." Alza, Palo Alto, CA.[3] Baile