INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15704, 'Gallium nitrate', 'Balsalazide', 'Moderate', 'Coadministration of 5-aminosalicylate with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of 5-aminosalicylate or its prodrugs has been associated with rare reports of renal impairment including minimal change nephropathy, acute and chronic interstitial nephritis, and renal failure.', NULL, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during 5-aminosalicylate therapy.', 'Synergism', 'Budesonide, Hydrocortisone, Cromoglicic acid, Prednisone, Prednisolone, Betamethasone, Beclomethasone dipropionate', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15719/', '[1] "Product Information. Canasa (mesalamine (5-aminosalicylic acid))." Axcan Scandipharm Inc, Birmingham, AL.[2] Schreiber S. Hamling J, Zehnter E, et al "Renal tubular dysfunction in patients with inflammatory bowel disease treated with aminosalicylate." Gut 40 (1997): 761-6[3] "Product Information. Lialda (mesalamine (5-aminosalicylic acid))." Shire US Inc, Florence, KY.[4] Novis BH, Korzets Z, Chen P, Bernheim J "Nephrotic syndrome after treatment with 5-aminosalicylic acid." Br Med J (Clin Res Ed) 296 (1988): 1442', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15705, 'Human botulinum neurotoxin A/B immune globulin', 'Gallium nitrate', 'Major', 'Coadministration of intravenous immune globulin preparations with nephrotoxic agents may potentiate the risk of renal impairment. Many commercially available intravenous formulations of immune globulin contain sucrose as a stabilizer.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.g., e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Clinicians should ensure that patients are not volume depleted prior to the initiation of immune globulin therapy. Monitoring of urine output and renal function tests, including the measurement of blood urea nitrogen (BUN) and serum creatinine, is recommended prior to the initial infusion and at appropriate intervals thereafter.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15720/', '[1] MMWR Morb Mortal Wkly Rep "Renal insufficiency and failure associated with immune globulin intravenous therapy -- United States, 1985-1998. Available from: URL: http://www.cdc.gov/mmwr/preview/mmwrhtml/mm4824a3.htm." ([1999 Jun 25]):[2] "Product Information. BabyBIG (botulism immune globulin)." FFF Enterprises, Temecula, CA.[3] Guo X, Nzerue C "How to prevent, recognize, and treat drug-induced nephrotoxicity. Available from: URL: http://www.ccjm.org/content/69/4/289.full.pdf." ([2002 Apr]):[4] Moses S "Nephrotoxic drugs, drug-induced nephrotoxicity. Available from: URL: http://www.fpnotebook.com/Renal/Pharm/NphrtxcDrgs.htm." ([2011 Nov 25]):[5] Perazella MA "Renal vulnerability to drug toxicity. Available from: URL: http://www.ccjm.org/content/69/4/289.full.pdf." ([2009]):[6] Levy JB, Pusey EY "Nephrotoxicity of intravenous immunoglobulin. Available from: URL: http://qjmed.oxfordjournals.org/content/93/11/751.full.pdf+html." ([2000]):[7] "Product Information. Respigam (respiratory syncytial virus immune globulin)." Medimmune Inc, Gaithersburg, MD.[8] "Product Information. Bivigam (immune globulin intravenous)." Biotest Pharmaceuticals Corporation, Boca Raton, FL.[9] "Product Information. Cytogam (cytomegalovirus immune globulin)." CSL Behring, King of Prussia, PA.[10] Naughton CA "Drug-induced nephrotoxicity. Available from: URL: http://www.aafp.org/afp/2008/0915/p743.html." ([2008]):[11] KDIGO. Kidney Disease Improving Global Outcomes "KDIGO clinical practice guideline for acute kidney injury. Available from: URL: http://www.kdigo.org/clinical_practice_guidelines/pdf/KDIGO-AKI-Suppl-Appendices-A-F_March2012.pdf." ([2012 Mar]):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15706, 'Gallium nitrate', 'Cidofovir', 'Major', 'Coadministration of cidofovir with other nephrotoxic agents may increase the risk of renal impairment due to additive effects on the kidney. Dose-related nephrotoxicity is the major toxicity of cidofovir. Cases of acute renal failure resulting in dialysis and/or contributing to death have occurred with as few as one or two doses of cidofovir.', NULL, 'Concomitant use of cidofovir with other potentially nephrotoxic agents (e.', NULL, 'Concomitant use of cidofovir with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; aminosalicylates; antiviral/antiretroviral agents such as acyclovir, adefovir, foscarnet, ganciclovir, and tenofovir; antineoplastics such as aldesleukin, cisplatin, clofarabine, ifosfamide, streptozocin, and high intravenous dosages of methotrexate; chelating agents such as deferasirox, deferoxamine, edetate disodium, and edetate calcium disodium; immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus; intravascular contrast media; intravenous bisphosphonates; intravenous pentamidine; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents; gallium nitrate; lithium; penicillamine) is considered contraindicated.', 'Synergism', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15721/', '[1] "Product Information. Vistide (cidofovir)." Gilead Sciences, Foster City, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15707, 'Gallium nitrate', 'Clofarabine', 'Moderate', 'Coadministration of clofarabine with other nephrotoxic agents may increase the risk of renal impairment due to additive effects on the kidney. Moreover, renal impairment secondary to the use of these agents may reduce the clearance of clofarabine, which is primarily eliminated by renal excretion. This may increase the risk of other adverse effects such as nausea, vomiting, stomatitis, hypertension, hypotension, myelosuppression, hemorrhage, and hepatotoxicity.', NULL, 'Drugs that are potentially nephrotoxic (e.', NULL, 'Drugs that are potentially nephrotoxic (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; aminosalicylates; antiviral/antiretroviral agents such as acyclovir, adefovir, cidofovir, foscarnet, ganciclovir, and tenofovir; antineoplastics such as aldesleukin, carboplatin, cisplatin, ifosfamide, streptozocin, and high intravenous dosages of methotrexate; chelating agents such as deferasirox, deferoxamine, edetate disodium, and edetate calcium disodium; immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus; intravenous bisphosphonates; intravenous pentamidine; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents; gallium nitrate; lithium; penicillamine) should be avoided during the 5 days of clofarabine administration if possible. Renal function should be evaluated prior to and during therapy, and administration discontinued immediately if substantial increases (e.g., grade 3 or higher) in creatinine are noted.', 'Excretion', 'Methotrexate, Fluorouracil, Mercaptopurine, Cladribine, Trifluridine, Floxuridine, Gemcitabine, Nelarabine, Cytarabine, Pralatrexate, Fludarabine, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15722/', '[1] "Product Information. Clolar (clofarabine)." sanofi-aventis, Bridgewater, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15708, 'Human cytomegalovirus immune globulin', 'Gallium nitrate', 'Major', 'Coadministration of intravenous immune globulin preparations with nephrotoxic agents may potentiate the risk of renal impairment. Many commercially available intravenous formulations of immune globulin contain sucrose as a stabilizer.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.g., e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Clinicians should ensure that patients are not volume depleted prior to the initiation of immune globulin therapy. Monitoring of urine output and renal function tests, including the measurement of blood urea nitrogen (BUN) and serum creatinine, is recommended prior to the initial infusion and at appropriate intervals thereafter.', 'Synergism', 'Bezlotoxumab, Palivizumab', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15723/', '[1] MMWR Morb Mortal Wkly Rep "Renal insufficiency and failure associated with immune globulin intravenous therapy -- United States, 1985-1998. Available from: URL: http://www.cdc.gov/mmwr/preview/mmwrhtml/mm4824a3.htm." ([1999 Jun 25]):[2] "Product Information. BabyBIG (botulism immune globulin)." FFF Enterprises, Temecula, CA.[3] Guo X, Nzerue C "How to prevent, recognize, and treat drug-induced nephrotoxicity. Available from: URL: http://www.ccjm.org/content/69/4/289.full.pdf." ([2002 Apr]):[4] Moses S "Nephrotoxic drugs, drug-induced nephrotoxicity. Available from: URL: http://www.fpnotebook.com/Renal/Pharm/NphrtxcDrgs.htm." ([2011 Nov 25]):[5] Perazella MA "Renal vulnerability to drug toxicity. Available from: URL: http://www.ccjm.org/content/69/4/289.full.pdf." ([2009]):[6] Levy JB, Pusey EY "Nephrotoxicity of intravenous immunoglobulin. Available from: URL: http://qjmed.oxfordjournals.org/content/93/11/751.full.pdf+html." ([2000]):[7] "Product Information. Respigam (respiratory syncytial virus immune globulin)." Medimmune Inc, Gaithersburg, MD.[8] "Product Information. Bivigam (immune globulin intravenous)." Biotest Pharmaceuticals Corporation, Boca Raton, FL.[9] "Product Information. Cytogam (cytomegalovirus immune globulin)." CSL Behring, King of Prussia, PA.[10] Naughton CA "Drug-induced nephrotoxicity. Available from: URL: http://www.aafp.org/afp/2008/0915/p743.html." ([2008]):[11] KDIGO. Kidney Disease Improving Global Outcomes "KDIGO clinical practice guideline for acute kidney injury. Available from: URL: http://www.kdigo.org/clinical_practice_guidelines/pdf/KDIGO-AKI-Suppl-Appendices-A-F_March2012.pdf." ([2012 Mar]):', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15709, 'Gallium nitrate', 'Deferasirox', 'Major', 'Theoretical concerns exist regarding the potential for increased risk of renal impairment during coadministration of deferasirox with other nephrotoxic agents. The use of deferasirox has been associated with postmarketing reports of acute renal failure, in some cases resulting in dialysis or even fatality.', NULL, 'Caution is advised if deferasirox is used in combination with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised if deferasirox is used in combination with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Serum creatinine and/or creatinine clearance should be closely monitored (e.g., prior to initiation of deferasirox therapy, then weekly during the first month after initiation or modification of therapy and monthly thereafter), especially in the elderly and patients with preexisting renal impairment, comorbid conditions, dehydration, or severe infections. Dosage reduction, interruption, or discontinuation should be considered in the presence of creatinine elevations. A progressive increase in serum creatinine beyond the age-appropriate upper limit of normal may warrant an interruption of therapy. Once the creatinine has returned to within the normal range, therapy may be reinitiated at a lower dose followed by a gradual dose escalation according to the product labeling, provided the clinical benefit is expected to outweigh potential risks.', 'Synergism', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15724/', '[1] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Rafat C, Fakhouri F, Ribeil JA, Delarue R, Le Quintrec M "Fanconi Syndrome Due to Deferasirox." Am J Kidney Dis (2009):[3] "Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15710, 'Ibandronate', 'Gallium nitrate', 'Moderate', 'Coadministration of bisphosphonates with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of bisphosphonates has been associated with nephrotoxicity manifested as deterioration of renal function and renal failure.', NULL, 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides, polypeptide and polymyxin antibiotics, vancomycin, amphotericin B, adefovir, cidofovir, tenofovir, foscarnet, cisplatin, gallium nitrate, lithium, mesalamine, certain immunosuppressants, intravenous pentamidine, high intravenous dosages of methotrexate, high dosages of nonsteroidal anti-inflammatory agents). Renal function and serum electrolytes should be monitored. Patients should have serum creatinine assessed prior to each treatment, and treatment should be withheld for renal deterioration.', 'Synergism', 'Tiludronic acid, Alendronic acid, Vosoritide, Denosumab, Cholecalciferol, Burosumab, Risedronic acid, Romosozumab', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15725/', '[1] "Product Information. Zometa (zoledronic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Kanis JA, Preston CJ, Yates AJ, Percival RC, Mundy KI, Russell RG "Effects of intravenous diphosphonates on renal function." Lancet 1 (1983): 1328[3] Chang JT, Green L, Beitz J "Renal failure with the use of zoledronic acid." N Engl J Med 349 (2003): 1676-9; discussion 1676-9[4] "Product Information. Aredia (pamidronate)." Novartis Pharmaceuticals, East Hanover, NJ.[5] Lockridge L, Papac RJ, Perazella MA "Pamidronate-associated nephrotoxicity in a patient with Langerhans''s histiocytosis." Am J Kidney Dis 40 (2002): E2[6] Osullivan TL, Akbari A, Cadnapaphornchai P "Acute renal failure associated with the administration of parenteral etidronate." Ren Fail 16 (1994): 767-73[7] Zazgornik J, Grafinger P, Biesenbach G, Hubmann R, Fridrik M "Acute renal failure and alendronate." Nephrol Dial Transplant 12 (1997): 2797-8[8] Banerjee D, Asif A, Striker L, Preston RA, Bourgoignie JJ, Roth D "Short-term, high-dose pamidronate-induced acute tubular necrosis: The postulated mechanisms of bisphosphonate nephrotoxicity." Am J Kidney Dis 41 (2003): E18[9] Markowitz GS, Fine PL, Stack JI, et al. "Toxic acute tubular necrosis following treatment with zoledronate (Zometa)." Kidney Int 64 (2003): 281-289[10] "Product Information. Boniva (ibandronate)." Roche Laboratories, Nutley, NJ.[11] "Product Information. Ostac (clodronate)." Hoffmann-La Roche Limited, Mississauga, IA.[12] Janssen Van Doorn K, Neyns B, Van Der Niepen P, Verbeelen D "Pamidronate-related nephrotoxicity (tubulointerstitial nephritis) in a patient with osteolytic bone metastases." Nephron 89 (2001): 467-8[13] "Product Information. Didronel Tablets (etidronate)." Procter and Gamble Pharmaceuticals, Cincinnati, OH.[14] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15711, 'Iothalamic acid', 'Gallium nitrate', 'Major', 'Concomitant use of intravascular radiocontrast media with other nephrotoxic agents may potentiate the risk of contrast-induced nephropathy and renal impairment. While the condition is usually transient and asymptomatic, it can be associated with increased risk of renal failure, dialysis, prolonged hospitalization, significant long-term morbidity, and mortality.', NULL, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy.', NULL, 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy. Any repeat procedures with contrast media, if necessary, should not occur until at least 48 to 72 hours after the previous contrast exposure and renal function has fully recovered.', 'Synergism', 'Ethiodized oil, Iopanoic acid, Tyropanoic acid, Iopodic acid', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15726/', '[1] Dubrovskaya Y, Prasad N, Lee Y, Esaian D, Figueroa DA, Tam VH "Risk factors for nephrotoxicity onset associated with polymyxin B therapy." J Antimicrob Chemother 70 (2015): 1903-7[2] Bentley ML, Corwin HL, Dasta J "Drug-induced acute kidney injury in the critically ill adult: recognition and prevention strategies." Crit Care Med 38(6 Suppl) (2010): S169-74[3] Kellum JA, Leblanc M, Venkataraman R "Acute renal failure." BMJ Clin Evid 9 (2008): 2001[4] van den Berk G, Tonino S, de Fijter C, Smit W, Schultz MJ "Bench-to-bedside review: Preventative measures for contrast-induced nephropathy in critically ill patients." Crit Care 9 (2005): 361-70[5] Marcos LA, Camins BC, Ritchie DJ, Casabar E, Warren DK "Acute renal insufficiency during telavancin therapy in clinical practice." J Antimicrob Chemother 67 (2012): 723-6[6] Bansal R, Kaplan AA, Aflieco F "Contrast-Induced Nephropathy. Available from: URL: http://emedicine.medscape.com/article/246751-overview." ([2016, Jan 18]):[7] Bennett WM, Porter GA "Nephrotoxicity of common drugs used by urologists." Urol Clin North Am 17 (1990): 145-56', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15712, 'Ganciclovir', 'Altretamine', 'Moderate', 'The use of ganciclovir with other potentially myelotoxic and/or nephrotoxic agents may increase the risk and severity of hematologic toxicity and/or nephrotoxicity due to both additive pharmacodynamic effects and additive effects on the kidney. Ganciclovir alone may cause severe leucopenia, neutropenia, anemia, thrombocytopenia, pancytopenia, bone marrow depression, aplastic anemia, increased serum creatinine levels, and acute renal failure, and the risk may theoretically increase when coadministered with other myelotoxic or nephrotoxic therapy.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks. Extreme caution is advised if they are used in patients who have recently received or are receiving treatment with myelotoxic and/or nephrotoxic drugs, and vice versa. If coadministration is required, reduced dosages of one or more of the drugs may be required, and the patient should be monitored for the development of hematologic and/or renal adverse effects both during and after discontinuation of therapy.', 'Synergism', 'Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Pegaspargase, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Anagrelide, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15727/', '[1] "Product Information. Valcyte (valganciclovir)" Roche Laboratories, Nutley, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Cytovene (ganciclovir)." Genentech, South San Francisco, CA.[5] "Product Information. Cytovene (ganciclovir)." Genentech, South San Francisco, CA.[6] Jung D, Griffy K, Dorr A "Effect of food on high-dose oral ganciclovir disposition in HIV-positive subjects." J Clin Pharmacol 39 (1999): 161-5[7] Lavelle J, Follansbee S, Trapnell CB, Buhles WC, Griffy KG, Jung D, Dorr A, Conner J "Effect of food on the relative bioavailability of oral ganciclovir." J Clin Pharmacol 36 (1996): 238-41', 1767369485);
