INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15861, 'Gemfibrozil', 'Colchicine', 'Moderate', 'Limited data suggest that coadministration of colchicine and fibric acid derivatives may increase the risk of myopathy. The mechanism of interaction has not been established. However, these agents are individually myotoxic and may have additive or synergistic effects when used together.', NULL, 'Caution is advised if colchicine is used in combination with fibric acid derivatives, particularly in the elderly and patients with underlying renal or hepatic impairment.', NULL, 'Caution is advised if colchicine is used in combination with fibric acid derivatives, particularly in the elderly and patients with underlying renal or hepatic impairment. Some experts recommend checking the creatine kinase level a week or two after coadministration of these agents and after any dose increase, although such monitoring does not reliably prevent the occurrence of severe myopathy. Patients should be advised to contact their physician if they experience symptoms of toxicity such as abdominal pain, nausea, vomiting, diarrhea, fatigue, myalgia, asthenia, hyporeflexia, paraesthesia, and numbness. The drugs should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Synergism', 'Mipomersen, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Colestipol, Niacin, Ezetimibe, Bempedoic acid, Colesevelam, Alirocumab', 'Pegloticase, Febuxostat, Sulfinpyrazone, Rasburicase, Probenecid', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15876/', '[1] "Product Information. Colcrys (colchi