INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15944, 'Gemifloxacin', 'Propofol', 'Moderate', 'Treatment with propofol may lead to prolongation of the QT interval; however, the extent of prolongation and its clinical impact is difficult to determine.', NULL, 'Caution and clinical monitoring is recommended if propofol is used concomitantly with other agents associated with QT interval prolongation.', NULL, 'Caution and clinical monitoring is recommended if propofol is used concomitantly with other agents associated with QT interval prolongation. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Thiopental, Alfentanil, Remifentanil, Isoflurane, Sodium oxybate, Methohexital, Fentanyl, Desflurane, Nitrous oxide, Sufentanil, Enflurane, More', 'Enoxacin, Nalidixic acid, Cinoxacin, Trovafloxacin, Delafloxacin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15959/', '[1] Wutzler A, De Asmundis C, Matsuda H, et.al "Effects of propofol on ventricular repolarization and incidence of malignant arrhythmias in adults." J Electrocardiol 51 (2018): 170-4[2] Whyte SD, Booker PD, Buckley DG "The Effects of Propofol and Sevoflurane on the QT Interval and Transmural Dispersion of Repolarization in Children." Anesth Analg 100 (2005): 71-77[3] Scalese MJ, Herring HR, Rathburn RC, Skrepnek GH, Ripley TL "Propofol-associated QTc prolongation." Ther Adv Drug Saf 7 (2016): 68-78[4] Paventi S, Santevecchi A, Ranieri R "Effects of sevoflurane versus propofol on QT interval." Minerva Anestesiol 67 (2001): 637-40[5] Kleinsasser A, Kuenszberg E, Loeckinger A, et.al "Sevoflurane, but not propofol, significantly prolongs the Q-T interval." Anesth Analg 90 (2000): 25-7[6] Kim DH, Kweon TD, Nam SB, Han DW, Cho WY, Lee JS "Effects of target concentration infusion of propofol and tracheal intubation on QTc interval." Anaesthesia 63 (2008): 1061-4[7] Staikou C, Stamelos M, Stavroulakis E "Impact of anaesthetic drugs and adjuvants on ECG markers of torsadogenicity." Br J Anaesth 112 (2014): 217-30[8] Kleinsasser A, Loeckinger A, Lindner KH, Keller C, Boehler M, Puehringer F "Reversing sevoflurane-associated Q-Tc prolongation by changing to propofol." Anaesthesia 56 (2001): 248-50[9] Hanci V, Aydin M, Yurtlu BS, et.al "Anesthesia induction with sevoflurane and propofol: evaluation of P-wave dispersion, QT and corrected QT intervals." Kaohsiung J Med Sci 26 (2010): 470-7[10] Toyoda T, Terao Y, Oji M, Okada M, Fukusaki M, Sumikawa K "The interaction of antiemetic dose of droperidol with propofol on QT interval during anesthetic induction." J Anesth 27 (2013): 885-9[11] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[12] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[13] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[14] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[15] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[16] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15945, 'Gemifloxacin', 'Sodium citrate', 'Moderate', 'Urinary alkalinizers such as citrates may decrease the solubility of fluoroquinolones in the urine and increase the risk of crystalluria.', NULL, 'Concomitant use should generally be avoided.', NULL, 'Concomitant use should generally be avoided. Patients receiving this combination should be monitored for signs of renal toxicity and crystalluria.', 'Excretion', 'Delafloxacin', 'Sorbitol, Sulfamethizole, Mannitol, Sodium bicarbonate, Sodium chloride, Chlorhexidine, Neomycin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15960/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15946, 'Anakinra', 'Gemtuzumab ozogamicin', 'Moderate', 'The use of interleukin-1 blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Interleukin-1 blockade alone may cause neutropenia and serious infections, and the risk may be increased in the presence of other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established. Until more information is available, interleukin-1 blockers should preferably not be administered to immunosuppressed patients, or they should be prescribed with extreme caution.', 'Synergism', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Tacrolimus, Abatacept, Apremilast, Inebilizumab, More', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15961/', '[1] "Product Information. Kineret (anakinra)." Amgen, Thousand Oaks, CA.[2] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[3] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (15947, 'Gemtuzumab ozogamicin', 'Anthrax vaccine', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'Pertuzumab, Cetuximab, Avelumab, Trastuzumab emtansine, Panitumumab, Ipilimumab, Necitumumab, Bevacizumab, Dostarlimab, Amivantamab, Ramucirumab, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/15962/', '[1] "Product Information. Imovax Rabies (rabies vaccine)." sanofi pasteur, Swiftwater, PA.[2] Department of Health. National Health Service "Immunisation Against Infectious Disease - "The Green Book". Chapter 6: Contraindications and special considerations. Available from: URL: https://assets.publishing.service.gov.uk/government/uploads/system/uploads/attachment_data/file/655225/Greenbook_chap" ([2017 Aug]):[3] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[4] "Product Information. Havrix (hepatitis A vaccine)." SmithKline Beecham, Philadelphia, PA.[5] "Product Information. Fluzone Influenza Vaccine (influenza virus vaccine)." Connaught, Swiftwater, PA.[6] CDC Centers for Disease Control and Prevention "General Best Practice Guidelines for Immunization: Altered Immunocompetence. Available from: URL: https://www.cdc.gov/vaccines/hcp/acip-recs/general-recs/immunocompetence.pdf." ([2017, Apr 17]):[7] "Product Information. Biothrax (anthrax vaccine adsorbed)." Emergent BioSolutions Inc., Lansing, MI.[8] "Product Information. Omnihib (haemophilus b conjugate vaccine)." SmithKline Beecham, Philadelphia, PA.[9] Cerner Multum, Inc. "Australian Product Information." O 0[10] "Product Information. Influenza Virus Vaccine, H5N1, Inactivated (influenza virus vaccine, H5N1, inactivated)." GlaxoSmithKline, Research Triangle Park, NC.', 1767369485);
