INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7532, 'Alfuzosin', 'Pseudoephedrine', 'Moderate', 'Sympathomimetic agents, such as pseudoephedrine, may antagonize the effects of antihypertensives that interfere with sympathetic activity, such as alpha- and beta-adrenergic blocking agents. In contrast, alpha- and beta-blockers may diminish the vasoconstrictive effects of sympathomimetics. The mechanism of this interaction has not been reported, but may involve competitive inhibition at the adrenergic receptor. Severe hypertension may occur in patients receiving beta-blockers.', NULL, 'Caution and close monitoring of blood pressure is recommended if pseudoephedrine is administered with these antihypertensives.', NULL, 'Caution and close monitoring of blood pressure is recommended if pseudoephedrine is administered with these antihypertensives. Discontinuation of the sympathomimetic may be necessary if hypertension develops.', 'Antagonism', 'Finasteride, Dutasteride, Solifenacin, Tadalafil', 'Phenylpropanolamine, Phenylephrine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7536/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Allegra-D (fexofenadine-pseudoephedrine)." Chattem Consumer Products, Chattanooga, TN.', 1767369485);
