INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7611, 'Esketamine', 'Acrivastine', 'Moderate', 'Concomitant use of esketamine with central nervous system (CNS) depressants may increase sedation and impairment of attention, judgment, thinking, reaction speed, and psychomotor skills.', NULL, 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants.', NULL, 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants. Due to the risk of delayed or prolonged sedation and other adverse effects, patients should be monitored for at least 2 hours after esketamine administration, followed by an assessment to determine when the patient is considered clinically stable and ready to leave the healthcare setting. Patients should be instructed not to engage in potentially hazardous activities, such as driving a motor vehicle or operating machinery, until the next day after a restful sleep.', 'Synergism', 'Ketotifen, Fexofenadine, Loratadine, Desloratadine, Azelastine, Terfenadine, Astemizole', 'Vilazodone, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, Bupropion, Halothane, Isoflurane, Nitrous oxide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7615/', '[1] "Product Information. Spravato (esketamine)." Janssen Pharmaceuticals, Titusville, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7612, 'Esmolol', 'Pseudoephedrine', 'Moderate', 'Sympathomimetic agents, such as pseudoephedrine, may antagonize the effects of antihypertensives that interfere with sympathetic activity, such as alpha- and beta-adrenergic blocking agents. In contrast, alpha- and beta-blockers may diminish the vasoconstrictive effects of sympathomimetics. The mechanism of this interaction has not been reported, but may involve competitive inhibition at the adrenergic receptor. Severe hypertension may occur in patients receiving beta-blockers.', NULL, 'Caution and close monitoring of blood pressure is recommended if pseudoephedrine is administered with these antihypertensives.', NULL, 'Caution and close monitoring of blood pressure is recommended if pseudoephedrine is administered with these antihypertensives. Discontinuation of the sympathomimetic may be necessary if hypertension develops.', 'Antagonism', 'Penbutolol, Carteolol', 'Phenylpropanolamine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7616/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Allegra-D (fexofenadine-pseudoephedrine)." Chattem Consumer Products, Chattanooga, TN.', 1767369485);
