INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7651, 'Iobenguane (I-123)', 'Pseudoephedrine', 'Moderate', 'Coadministration with drugs that block norepinephrine uptake or deplete norepinephrine stores may decrease iobenguane I-123 uptake in neuroendocrine tumors and lead to false-negative imaging results.', NULL, 'When medically feasible, any drug that blocks norepinephrine uptake or deplete norepinephrine stores should be discontinued for at least five biological half-lives before iobenguane I-123 administration.', NULL, 'When medically feasible, any drug that blocks norepinephrine uptake or deplete norepinephrine stores should be discontinued for at least five biological half-lives before iobenguane I-123 administration.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7655/', '[1] "Product Information. AdreView (iobenguane I-123)." GE Healthcare, Princeton, NJ.[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7652, 'Iobenguane (I-131)', 'Pseudoephedrine', 'Major', 'Coadministration with drugs that reduce catecholamine uptake or deplete catecholamine stores may interfere with iobenguane I-131 uptake into neuroendocrine tumors such as pheochromocytoma and paraganglioma that express high levels of norepinephrine transporter on their cell surfaces. Since iobenguane is similar in structure to norepinephrine and is subject to the same uptake and accumulation pathways as norepinephrine, drugs that alter norepinephrine disposition in adrenergic nerve terminals and presynaptic storage vesicles will likewise affect iobenguane.', NULL, 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131.', NULL, 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131. Do not administer these drugs until at least 7 days after each iobenguane I-131 dose. Patients should be monitored for the occurrence of clinically significant withdrawal symptoms, especially patients with elevated levels of circulating catecholamines and their metabolites.', 'Distribution', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7656/', '[1] "Product Information. Azedra (iobenguane I-131)." Progenics Pharmaceuticals, Inc., New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7653, 'Ipratropium', 'Acrivastine', 'Moderate', 'The potential exists for additive anticholinergic effects such as mydriasis, blurred vision, heat intolerance, fever, dry mouth, tachycardia, urinary retention, constipation, and glaucoma (onset or exacerbation) when topical or inhaled anticholinergic agents are used with each other or with other agents that possess anticholinergic properties. The risk of systemic anticholinergic effects following topical administration depends on variables such as strength of the product, size of the application area, frequency of application, and use of occlusive dressing. Systemic effects are uncommon following oral inhalation or nasal administration due to the poor absorption of quaternary ammonium compounds from gastrointestinal and nasal mucosa.', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).', NULL, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide). Caution is advised if concomitant use cannot be avoided, particularly in the elderly and those with significantly impaired renal and/or hepatic function.', 'Synergism', 'Ciclesonide, Mometasone furoate, Budesonide, Flunisolide, Fluticasone, Cromoglicic acid, Betamethasone, Nedocromil, Triamcinolone, Beclomethasone dipropionate, Flunisolide, More', 'Ketotifen, Levocetirizine, Cetirizine, Fexofenadine, Loratadine, Trimethobenzamide, Desloratadine, Azelastine, Terfenadine, Astemizole', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7657/', '[1] "Product Information. Qbrexza (glycopyrrolate topical)." Dermira, Inc., Menlo Park, CA.[2] "Product Information. Anoro Ellipta (umeclidinium-vilanterol)." GlaxoSmithKline, Research Triangle Park, NC.[3] "Product Information. Combivent (albuterol-ipratropium)." Boehringer-Ingelheim, Ridgefield, CT.[4] "Product Information. Atrovent (ipratropium)." Boehringer-Ingelheim, Ridgefield, CT.[5] "Product Information. Yupelri (revefenacin)." Mylan Specialty, Morgantown, WV.[6] "Product Information. Tudorza Pressair (aclidinium)." Forest Pharmaceuticals, St. Louis, MO.[7] "Product Information. Spiriva (tiotropium)." Boehringer Ingelheim, Ridgefield, CT.[8] Cole JM, Sheehan AH, Jordan JK "Concomitant use of ipratropium and tiotropium in chronic obstructive plmonary disease." Ann Pharmacother 46 (2012): 1717-21[9] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[10] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[11] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[12] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
