INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7808, 'Aminolevulinic acid (topical)', 'Doxycycline', 'Moderate', 'Concomitant use of aminolevulinate topical preparations with other known photosensitizing agents may enhance the phototoxic reaction to photodynamic therapy. Medicinal products with known phototoxic or photoallergic potential include fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, and hypericin extracts (e.g., St John''s Wort).', NULL, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', NULL, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid exposure of treated areas to sunlight or bright indoor lights (e.g., examination lamps, operating room lamps, tanning beds, lights at close proximity) during the period between application of aminolevulinic acid or methyl aminolevulinate and photoactivation, and for 48 hours post-illumination. Concomitant use with other topical medicinal products should be avoided.', 'Synergism', 'Acetylsalicylic acid, Amphotericin B, Miconazole, Clotrimazole, Hydrocortisone, Metronidazole, Prednisolone, Triamcinolone, Epinephrine, Chlorhexidine, Neomycin, Dexamethasone', 'Pertuzumab, Olaparib, Rucaparib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, Enasidenib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7812/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Metvixia (methyl aminolevulinate topical)." Galderma Laboratories Inc, Cranbury, NJ.[4] "Product Information. Levulan Kerastick (aminolevulinic acid)" Berlex, Richmond, CA.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7809, 'Aminophylline', 'Doxycycline', 'Moderate', 'Coadministration of a tetracycline may decrease theophylline plasma clearance and increase theophylline levels. Data are available for minocycline, tetracycline, and doxycycline, but are conflicting in some cases. This interaction appears to be more problematic if a tetracycline is administered for more than five days. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity.', NULL, 'If these drugs are given concurrently, close clinical and laboratory monitoring of response and tolerance is recommended.', NULL, 'If these drugs are given concurrently, close clinical and laboratory monitoring of response and tolerance is recommended. Patients should be advised to notify their physician if they experience any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeats. It may be necessary to reduce theophylline dosage.', 'Metabolism', 'Tigecycline, Sarecycline, Omadacycline, Eravacycline, Miconazole, Clotrimazole, Epinephrine, Chlorhexidine, Neomycin', 'Omalizumab, Tezepelumab, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Roflumilast, Zafirlukast, Bromotheophylline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7813/', '[1] McCormack JP, Reid SE, Lawson LM "Theophylline toxicity induced by tetracycline." Clin Pharm 9 (1990): 546-9[2] Gotz VP, Ryerson GG "Evaluation of tetracycline on theophylline disposition in patients with chronic obstructive airways disease." Drug Intell Clin Pharm 20 (1986): 694-6[3] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part I)." Clin Pharmacokinet 20 (1991): 66-80[4] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[5] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767369485);
