INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7819, 'Doxycycline', 'Balsalazide', 'Moderate', 'Balsalazide may be rendered less effective when administered with certain antibiotics. Balsalazide is acted upon in the lower gut by bacterial azoreduction to produce mesalamine, the therapeutically active ingredient. Although no studies have been done, it is theorized that oral antibiotics can interfere with this reduction to mesalamine.', NULL, 'If the patient must receive oral antibiotics, consideration should be given to using another 5-aminosalicylate such as mesalamine.', NULL, 'If the patient must receive oral antibiotics, consideration should be given to using another 5-aminosalicylate such as mesalamine.', 'Antagonism', 'Tigecycline, Sarecycline, Omadacycline, Eravacycline, Acetylsalicylic acid, Miconazole, Clotrimazole, Hydrocortisone, Triamcinolone, Epinephrine, Chlorhexidine, Dexamethasone', 'Beclomethasone dipropionate, Betamethasone, Cromoglicic acid, Olsalazine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7823/', '[1] "Product Information. Colazal (balsalazide)" Salix Pharmaceuticals, Palo Alto, CA.', 1767369485);
