INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7913, 'Doxycycline', 'Methotrexate', 'Moderate', 'Concomitant use of tetracyclines may elevate or reduce serum methotrexate concentrations. One mechanism may involve displacement of methotrexate from its binding to plasma proteins. Individual cases of methotrexate toxicity have been reported with doxycycline and tetracycline. In contrast, it has also been reported that oral antibiotics such as tetracyclines may reduce the intestinal absorption of methotrexate or interfere with its enterohepatic circulation or by inhibiting bowel flora and suppressing the metabolism of methotrexate by bacteria. However, the clinical significance of this effect has not been established.', NULL, 'If these drugs must be used together, close monitoring for evidence of serious methotrexate toxicity or reduced therapeutic efficacy is recommended.', NULL, 'If these drugs must be used together, close monitoring for evidence of serious methotrexate toxicity or reduced therapeutic efficacy is recommended. If broad-spectrum antibiotic coverage is needed during high-dose methotrexate therapy, the use of an alternate anti-infective may be considered.', 'Absorption, Distribution', 'Tigecycline, Sarecycline, Omadacycline, Eravacycline, Chlorhexidine', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Abatacept, Upadacitinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7917/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Tortajada-Ituren JJ, Ordovas-Baines JP, Llopis-Salvia P, Jimenez-Torres NV "High-dose methotrexat