INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7957, 'Doxycycline', 'Sucralfate', 'Moderate', 'INTERVAL: Simultaneous administration of sucralfate may interfere with the absorption of tetracyclines and reduce their oral bioavailability. Sucralfate is an aluminum salt of a sulfated disaccharide. The aluminum moiety can dissociate at gastric pH and chelate with tetracyclines to form an insoluble complex that is poorly absorbed from the gastrointestinal tract. Reduced antibiotic efficacy may occur.', NULL, 'To minimize the potential for interaction, tetracyclines should be administered at least 2 hours before sucralfate.', NULL, 'To minimize the potential for interaction, tetracyclines should be administered at least 2 hours before sucralfate.', 'Absorption', 'Tigecycline, Eravacycline, Neomycin, Miconazole', 'Tinidazole, Dexlansoprazole, Esomeprazole, Ranitidine, Vonoprazan, Levofloxacin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7961/', '[1] "Product Information. Carafate (sucralfate)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[3] McCarthy DM "Sucralfate." N Engl J Med 325 (1991): 1017-25[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[6] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7958, 'Iron sucrose', 'Doxycycline', 'Moderate', 'INTERVAL: Coadministration with sucroferric oxyhydroxide may reduce the oral bioavailability of tetracyclines. The proposed mechanism is chelation of tetracyclines to the ferric ion component of sucroferric oxyhydroxide, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Doxycycline should be administered at least 1 hour before sucroferric oxyhydroxide.', NULL, 'Doxycycline should be administered at least 1 hour before sucroferric oxyhydroxide. Omadacycline administration with sucroferric oxyhydroxide should be separated by 4 hours. No dosing recommendations are available for use with other tetracyclines, although patients should consider following the same precaution.', 'Absorption', 'Tigecycline, Eravacycline, Acetylsalicylic acid, Amphotericin B, Miconazole, Clotrimazole, Hydrocortisone, Metronidazole, Prednisolone, Triamcinolone, Epinephrine, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, Arginine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7962/', '[1] Neuvonen PJ "Interactions with the absorption of tetracyclines." Drugs 11 (1976): 45-54[2] "Product Information. Nuzyra (omadacycline)." Paratek Pharmaceuticals, Inc., Boston, MA.[3] "Product Information. Velphoro (sucroferric oxyhydroxide)." Fresenius Medical Care North America, Lexington, MA.[4] Campbell NR, Hasinoff BB "Iron supplements: a common cause of drug interactions." Br J Clin Pharmacol 31 (1991): 251-5[5] Greenberger NJ "Absorption of tetracyclines: inte