INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7959, 'Doxycycline', 'Theophylline', 'Moderate', 'Coadministration of a tetracycline may decrease theophylline plasma clearance and increase theophylline levels. Data are available for minocycline, tetracycline, and doxycycline, but are conflicting in some cases. This interaction appears to be more problematic if a tetracycline is administered for more than five days. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity.', NULL, 'If these drugs are given concurrently, close clinical and laboratory monitoring of response and tolerance is recommended.', NULL, 'If these drugs are given concurrently, close clinical and laboratory monitoring of response and tolerance is recommended. Patients should be advised to notify their physician if they experience any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeats. It may be necessary to reduce theophylline dosage.', 'Metabolism', 'Tigecycline, Sarecycline, Omadacycline, Eravacycline, Clotrimazole, Chlorhexidine, Neomycin, Miconazole', 'Omalizumab, Tezepelumab, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Roflumilast, Zafirlukast, Bromotheophylline', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7963/', '[1] McCormack JP, Reid SE, Lawson LM "Theophylline toxicity induced by tetracycline." Clin Pharm 9 (1990): 546-9[2] Gotz VP, Ryerson GG "Evaluation of tetracycline on theophylline disposition in patients with chron