INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7994, 'Azilsartan medoxomil', 'Ketorolac', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of angiotensin II receptor antagonists. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and angiotensin II receptor antagonists may cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Homatropine, Atropine, Scopolamine, Methscopolamine, Ephedrine, Tropicamide, More', 'Sacubitril', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7998/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs: a randomized, double-blind, placebo-controlled trial of ibuprofen compared with acetaminophen." Ann Intern Med 107 (1987): 628-35[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Toradol (ketorolac)." Roche Laboratories, Nutley, NJ.[4] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.[5] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7995, 'Balsalazide', 'Ketorolac', 'Moderate', 'Coadministration of 5-aminosalicylate with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of 5-aminosalicylate or its prodrugs has been associated with rare reports of renal impairment including minimal change nephropathy, acute and chronic interstitial nephritis, and renal failure.', NULL, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during 5-aminosalicylate therapy.', 'Synergism', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Homatropine, Atropine, Scopolamine, Methscopolamine, Ephedrine, Tropicamide, More', 'Beclomethasone dipropionate, Cromoglicic acid', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/7999/', '[1] "Product Information. Canasa (mesalamine (5-aminosalicylic acid))." Axcan Scandipharm Inc, Birmingham, AL.[2] Schreiber S. Hamling J, Zehnter E, et al "Renal tubular dysfunction in patients with inflammatory bowel disease treated with aminosalicylate." Gut 40 (1997): 761-6[3] "Product Information. Lialda (mesalamine (5-aminosalicylic acid))." Shire US Inc, Florence, KY.[4] Novis BH, Korzets Z, Chen P, Bernheim J "Nephrotic syndrome after treatment with 5-aminosalicylic acid." Br Med J (Clin Res Ed) 296 (1988): 1442', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7996, 'Baricitinib', 'Ketorolac', 'Moderate', 'Baricitinib and tofacitinib, both Janus kinase (JAK) inhibitors, have been associated with an increased risk of diverticulitis (DV) and gastrointestinal (GI) perforation, particularly in patients with risk factors (e.g., history of diverticulosis or diverticulitis, concomitant use of other agents associated with DV).', NULL, 'Caution is recommended when using baricitinib or tofacitinib in patients with a history of diverticular disease and in patients receiving long-term concomitant treatment with drugs associated with an increased risk of DV and/or GI perforation, such as aspirin, NSAIDs, corticosteroids, and opioids.', NULL, 'Caution is recommended when using baricitinib or tofacitinib in patients with a history of diverticular disease and in patients receiving long-term concomitant treatment with drugs associated with an increased risk of DV and/or GI perforation, such as aspirin, NSAIDs, corticosteroids, and opioids. Patients should be advised to contact their healthcare provider if they experience signs of DV or GI perforation, such as severe abdominal pain, fever, nausea, or vomiting.', 'Synergism', 'Rabeprazole, Misoprostol, Glucosamine, Chondroitin sulfate, Rofecoxib, Esomeprazole, Valdecoxib, Homatropine, Atropine, Scopolamine, Methscopolamine, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8000/', '[1] Medicines and Healthcare products Regulatory Agency "Baricitinib (Olumiant): increased risk of diverticulitis, particularly in patients with risk factors. Available from: URL: https://www.gov.uk/drug-safety-update/baricitinib-olumiant-increased-risk-of-diverticulitis-particularly-in-patients-with-risk-fac" ([2020, Aug 26]):[2] Strate LL, Liu YL, Huang ES, Giovannucci EL, Chan AT "Use of Aspirin or Nonsteroidal Anti-inflammatory Drugs Increases Risk for Diverticulitis and Diverticular Bleeding." Gastroenterology 140 (2011): 1427-33', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7997, 'Bedaquiline', 'Ketorolac', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Homatropine, Atropine, Scopolamine, Methscopolamine, Ephedrine, Tropicamide, More', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Rifapentine, Trimethoprim, Rifampicin, Cycloserine, Rifabutin, Isoniazid, Ethambutol, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8001/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[3] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (7998, 'Benazepril', 'Ketorolac', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of ACE inhibitors. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and ACE inhibitors may also cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function. Acute renal failure may occur, although effects are usually reversible. Chronic use of NSAIDs alone may be associated with renal toxicities, including elevations in serum creatinine and BUN, tubular necrosis, glomerulitis, renal papillary necrosis, acute interstitial nephritis, nephrotic syndrome, and renal failure.', NULL, 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Misoprostol, Chondroitin sul