INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8034, 'Clopidogrel', 'Ketorolac', 'Moderate', 'The coadministration of clopidogrel with naproxen resulted in occult gastrointestinal blood loss in healthy volunteers. The mechanism has not been described, but may be due to additive platelet inhibition. The coadministration of nonsteroidal anti-inflammatory drugs (NSAIDs) and clopidogrel should be undertaken with extreme caution. Additionally, some NSAIDs are substrates for the CYP450 isoenzyme 2C9. Clopidogrel inhibits this isoenzyme and therefore may lead to decreased metabolism of these NSAIDs.', NULL, 'Close observation for increased NSAID toxicity is recommended if these agents are coadministered with clopidogrel.', NULL, 'Close observation for increased NSAID toxicity is recommended if these agents are coadministered with clopidogrel. Patients should also be advised to promptly report any signs of GI bleeding to their caregiver, including pain, swelling, dizziness, weakness, bloody or coffee-ground emesis, or red or black stools, and to avoid any over-the-counter NSAID products.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8038/', '[1] "Product Information. Plavix (clopidogrel)." Bristol-Myers Squibb, Princeton, NJ.', 1767365203);
