INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8183, 'Pegaspargase', 'Ketorolac', 'Moderate', 'Asparaginase therapy may lead to imbalances in coagulation factors predisposing the patient to bleeding or thrombosis. Theoretically, concurrent use of anticoagulants and asparaginase may increase the risk of bleeding. Hemorrhages grade 3 and above and CNS hemorrhages have been observed with asparaginase treatment.', NULL, 'Coagulation parameters should be monitored at baseline and periodically during concomitant treatment with asparaginase and anticoagulants such as heparin, dipyridamole, aspirin and nonsteroidal anti-inflammatory drugs.', NULL, 'Coagulation parameters should be monitored at baseline and periodically during concomitant treatment with asparaginase and anticoagulants such as heparin, dipyridamole, aspirin and nonsteroidal anti-inflammatory drugs. Patients should be advised to promptly report any signs and symptoms of bleeding.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8187/', '[1] Caruso V, Iacoviello L, Di Castelnuovo A, et.al "Thrombotic complications in childhood acute lymphoblastic leukemia: a meta-analysis of 17 prospective studies comprising 1752 pediatric patients." Blood 108 (2006): 2216-22[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Duarte X, Esteves S, Neto AM, Pereira F "Incidence and risk factors for central nervous system thrombosis in paediatric acute lymphoblastic leukaemia during intensive asparaginase treatment: a single-centre study." Br J Haematol 174 (2016): 280-91[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Kieslich M, Porto L, Lanfermann H, Jacobi G