INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8242, 'Telbivudine', 'Ketorolac', 'Moderate', 'Because telbivudine is primarily eliminated by renal excretion, coadministration with drugs that affect renal function may alter the plasma concentrations of telbivudine and/or the coadministered drug.', NULL, 'Caution is advised if telbivudine is prescribed in combination with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if telbivudine is prescribed in combination with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during therapy with telbivudine.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8246/', '[1] "Product Information. Tyzeka (telbivudine)." IDEC Pharmaceuticals Corporation, San Diego, CA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8243, 'Telmisartan', 'Ketorolac', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of angiotensin II receptor antagonists. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and angiotensin II receptor antagonists may cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8247/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs: a randomized, double-blind, placebo-controlled trial of ibuprofen compared with acetaminophen." Ann Intern Med 107 (1987): 628-35[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Toradol (ketorolac)." Roche Laboratories, Nutley, NJ.[4] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.[5] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8244, 'Temsirolimus', 'Ketorolac', 'Major', 'Coadministration of macrolide immunosuppressants with other nephrotoxic agents may increase the risk and/or severity of renal impairment due to additive adverse effects on the kidney. Clinical experience in coadministration with cyclosporine indicates increased renal toxicity as evidenced by increased serum creatinine and decreased glomerular filtration rate. An interaction with ibuprofen resulting in acute renal failure was suspected in two liver transplant patients who had been stabilized on tacrolimus.', NULL, 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.', NULL, 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; cyclosporine; deferasirox; gallium nitrate; lithium; mesalamine; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be closely monitored both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms that may indicate nephrotoxicity such as decreased urine output, sudden weight gain, fluid retention, edema, or shortness of breath.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8248/', '[1] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Sheiner PA, Mor E, Chodoff L, Glabman S, Emre S, Schwartz ME, Miller CM "Acute renal, failure associated with the use of ibuprofen in two liver transplant recipients on FK506." Transplantation 57 (1994): 1132-3[4] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365203);
