INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8395, 'Acyclovir', 'Trospium', 'Moderate', 'Theoretically, coadministration of trospium chloride with other drugs that are eliminated by active tubular secretion may result in increased plasma concentrations of trospium and/or the coadministered drug(s). The mechanism is competitive inhibition of renal excretion. Drugs that are thought to undergo active tubular secretion include acyclovir/valacyclovir, cidofovir, cimetidine, digoxin, flecainide, ganciclovir/valganciclovir, metformin, midodrine, morphine, pancuronium, procainamide, quinidine, ranitidine, tenofovir, triamterene, and vancomycin.', NULL, 'Patients receiving trospium chloride in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', NULL, 'Patients receiving trospium chloride in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8399/', '[1] "Product Information. Sanctura (trospium chloride)." Odyssey Pharmaceutical, East Hanover, NJ.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Sanctura (trospium chloride)." Odyssey Pharmaceutical, East Hanover, NJ.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8396, 'Acyclovir', 'Varicella Zoster Vaccine (Recombinant)', 'Major', 'Concomitant use of live, attenuated varicella virus vaccine or zoster vaccine with acyclovir, famciclovir, or valacyclovir may result in diminished immunologic response to the vaccine. Although clinical data are lacking, theoretical concern exists that antiviral activity against the vaccine strain may prevent a sufficient degree of replication to occur in order to induce a protective immune response. Acyclovir, specifically, has demonstrated activity against the Oka strain of live, attenuated varicella-zoster virus used in the vaccines.', NULL, 'Acyclovir, famciclovir, and valacyclovir should not be used for at least 24 hours before administration of live, attenuated varicella virus vaccine or zoster vaccine.', NULL, 'Acyclovir, famciclovir, and valacyclovir should not be used for at least 24 hours before administration of live, attenuated varicella virus vaccine or zoster vaccine. In addition, these antiviral agents should generally be avoided for 14 days after vaccination.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8400/', '[1] "Product Information. Zovirax (acyclovir)." Mylan Pharmaceuticals Inc, Morgantown, WV.[2] US Centers for Disease Control and Prevention (CDC) "Recommended immunization schedule for adults aged 19 years or older, United States, 2017 Available from: URL: https://www.cdc.gov/vaccines/schedules/downloads/adult/adult-combined-schedule-bw.pdf." ([2017, Feb]):', 1767365203);
