INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328890, 'Isocarboxazid', 'Codeine', 'Major', 'Coadministration of narcotic analgesics with monoamine oxidase inhibitors (MAOIs) has been associated with rare reports of anxiety, confusion, hypotension, respiratory depression, cyanosis, and coma. The mechanism of interaction is unknown, but may involve potentiation of central nervous system (CNS) and respiratory depressant effects by MAOIs.', 'DDInter', 1767317475, 'Narcotic analgesics should generally not be used with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with opioids.', 'Others', 'Narcotic analgesics should generally not be used with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
