INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1328946, 'Codeine', 'Mirabegron', 'Moderate', 'Drugs that are inhibitors of CYP450 2D6 may interfere with the analgesic effect of codeine. The mechanism is decreased in vivo conversion of codeine to morphine, a metabolic reaction mediated by CYP450 2D6.', 'DDInter', 1767317475, 'The possibility of reduced or inadequate pain relief should be considered in patients receiving codeine with drugs that inhibit CYP450 2D6. An increase in the codeine dosage or a different analgesic agent may be necessary in patients requiring therapy with CYP450 2D6 inhibitors.', 'Metabolism', 'The possibility of reduced or inadequate pain relief should be considered in patients receiving codeine with drugs that inhibit CYP450 2D6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
