INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8635, 'Betrixaban', 'Nifedipine', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of betrixaban, which is a substrate of the efflux transporter.', NULL, 'Caution is advised when betrixaban is used with P-gp inhibitors.', NULL, 'Caution is advised when betrixaban is used with P-gp inhibitors. Closer monitoring of the pharmacologic effects of betrixaban may be appropriate whenever a P-gp inhibitor is added to or withdrawn from therapy. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women).', 'Absorption', 'Amlodipine, Nisoldipine, Nicardipine, Isradipine, Clevidipine, Levamlodipine, Nimodipine, Amlodipine, Amlodipine, Propranolol, Metoprolol, More', 'Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, Streptokinase, Tinzaparin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8639/', '[1] "Product Information. Bevyxxa (betrixaban)." Portola Pharmaceuticals, South San Francisco, CA.[2] "Product Information. Bevyxxa (betrixaban)." Portola Pharmaceuticals, South San Francisco, CA.[3] "Product Information. Bevyxxa (betrixaban)." Portola Pharmaceuticals, South San Francisco, CA.[4] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8636, 'Bexarotene', 'Nifedipine', 'Moderate', 'Coadministration with bexarotene may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. The proposed mechanism is increased clearance due to induction of CYP450 3A4 activity by bexarotene.', NULL, 'Caution is advised if bexarotene is prescribed concomitantly with medications that are primarily metabolized by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if bexarotene is prescribed concomitantly with medications that are primarily metabolized by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever bexarotene is added to or withdrawn from therapy.', 'Metabolism', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Niraparib, Selinexor, More', 'Clevidipine, Celecoxib, Propranolol, Carvedilol, Nebivolol, Bisoprolol, Sotalol, Acetylsalicylic acid, Atenolol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8640/', '[1] Wakelee HA, Takimoto CH, Lopez-Anaya A, et al. "The effect of bexarotene on atorvastatin pharmacokinetics: results from a phase I trial of bexarotene plus chemotherapy in patients with advanced non-small cell lung cancer." Cancer Chemother Pharmacol 69 (2012): 563-71[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Targretin (bexarotene)" Ligand Pharmaceuticals, San Diego, CA.[4] Cerner Multum, Inc. "UK Summary of Product Char