INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8642, 'Bosutinib', 'Nifedipine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of bosutinib, which is a substrate of the isoenzyme and efflux transporter.', NULL, 'Caution is advised when bosutinib is used with CYP450 3A4 or P-gp inhibitors.', NULL, 'Caution is advised when bosutinib is used with CYP450 3A4 or P-gp inhibitors. Patients should be monitored more closely for development of adverse effects such as diarrhea, nausea, vomiting, abdominal pain, myelosuppression, hepatotoxicity, and fluid retention (e.g., pericardial effusion, pleural effusion, pulmonary edema, peripheral edema).', 'Metabolism', 'Felodipine, Nisoldipine, Nicardipine, Isradipine, Clevidipine, Celecoxib, Nimodipine, Felodipine, Propranolol, Metoprolol, Carvedilol, More', 'Dacomitinib, Tepotinib, Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8646/', '[1] "Product Information. Bosulif (bosutinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Bosulif (bosutinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Bosulif (bosutinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[6] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8643, 'Brexpiprazole', 'Nifedipine', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Acetylsalicylic acid, Ivabradine', 'Droperidol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8647/', '[1] "Product Information. Seroquel (quetiapine)." Zeneca Pharmaceuticals, Wilmington, DE.[2] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Risperdal (risp