INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8827, 'Nifedipine', 'Ixabepilone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ixabepilone, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when ixabepilone is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when ixabepilone is prescribed with CYP450 3A4 inhibitors. Pharmacologic response to ixabepilone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the ixabepilone dosage adjusted as necessary. Frequent monitoring of peripheral blood counts between treatment cycles is recommended, and patients should be advised to contact their physician if they experience signs and symptoms of ixabepilone toxicities such as infection or burning, tingling, or numbness in the hands and feet.', 'Metabolism', 'Felodipine, Amlodipine, Nisoldipine, Nicardipine, Isradipine, Clevidipine, Levamlodipine, Celecoxib, Nimodipine, Amlodipine, Felodipine, More', 'Valrubicin, Plicamycin, Bleomycin, Mitomycin, Dactinomycin, Epirubicin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8831/', '[1] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8828, 'Nifedipine', 'Ixekizumab', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Clevidipine, Mibefradil, Celecoxib, Propranolol, Metoprolol, Carvedilol, Nebivolol, Bisoprolol, Ivabradine, Sotalol, Acetylsalicylic acid, Atenolol', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Sutimlimab, Risankizumab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8832/', '[1] "Product Information. Skyrizi (risankizumab)." AbbVie US LLC, North Chicago, IL.[2] "Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Gamifant (emapalumab)." Sobi Inc, Ardmore, PA.[5] "Product Information. Kevzara (sarilumab)." sanofi-aventis, Bridgewater, NJ.[6] "Product Information. Simponi (golimumab)." Centocor Inc, Malvern, PA.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] "Product Information. Sylvant (siltuximab)." Janssen Biotech, Inc., Horsham, PA, PA.[9] "Product Information. Stelara (ustekinumab)." Centocor Inc, Malvern, PA.[10] "Product Information. Ilumya (tildrakizumab)." Merck & Company Inc, Whitehouse Station, NJ.[11] "Product Information. Actemra (tocilizumab)." Genentech, South San Francisco, CA.[12] "Product Information. Amevive (alefacept)." Biogen, Cambridge, MA.[13] "Product Information. Remicade (infliximab)." Centocor Inc, Malvern, PA.[14] "Product Information. Taltz Autoinjector (ixekizumab)." Eli Lilly and Company, Indianapolis, IN.[15] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[16] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[17] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine