INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8862, 'Nifedipine', 'Mefloquine', 'Moderate', 'Mefloquine is a myocardial depressant and can cause ECG abnormalities. Theoretically, coadministration with other agents that can affect cardiac conduction (e.g., antiarrhythmic agents, beta blockers, calcium channel blockers, certain antihistamines, tricyclic antidepressants, phenothiazines, some neuroleptics) may result in additive effects and increased risk of ventricular arrhythmias including ventricular tachycardia and torsade de pointes.', NULL, 'Caution and clinical monitoring are recommended if mefloquine is used in combination with other drugs that can prolong the QT interval or otherwise affect cardiac conduction.', NULL, 'Caution and clinical monitoring are recommended if mefloquine is used in combination with other drugs that can prolong the QT interval or otherwise affect cardiac conduction. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes or other arrhythmia such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Artesunate, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Halofantrine, Quinine, Primaquine, Proguanil, Sulfadoxine, Chloroquine', 'Clevidipine, Celecoxib, Acetylsalicylic acid', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8866/', '[1] "Product Information. Lariam (mefloquine)." Roche Laboratories, Nutley, NJ.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."