INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8904, 'Nifedipine', 'Nilotinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the plasma concentrations of nilotinib, which is a substrate of both the isoenzyme and the efflux transporter. Data are not available for nilotinib in combination with other CYP450 3A4 or P-glycoprotein inhibitors. Theoretically, a reverse interaction may also occur, since many CYP450 3A4 and P-glycoprotein inhibitors are also substrates, and nilotinib is an inhibitor of both.', NULL, 'Caution is advised if nilotinib is prescribed in combination with CYP450 3A4 and/or P-glycoprotein inhibitors.', NULL, 'Caution is advised if nilotinib is prescribed in combination with CYP450 3A4 and/or P-glycoprotein inhibitors. Pharmacologic response to nilotinib should be monitored more closely whenever a CYP450 3A4 or P-glycoprotein inhibitor is added to or withdrawn from therapy, and the nilotinib dosage adjusted as necessary. Patients should have frequent ECGs and be monitored for arrhythmias when QT interval is prolonged. A QTc interval exceeding 480 msec will require suspension of nilotinib therapy and immediate action to correct any concomitant risk factors before resuming treatment. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Patients should also be monitored for altered efficacy and safety of the concomitant administered drug.', 'Metabolism', 'Clevidipine, Nicardipine, Acetylsalicylic acid, Nebivolol, Bisoprolol, Atenolol', 'Dacomitinib, Tepotinib, Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8908/', '[1] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (8905, 'Nifedipine', 'Nintedanib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and CYP450 3A4 may increase the plasma concentrations of nintedanib, which is a substrate of the efflux transporter and a minor substrate of the isoenzyme.', NULL, 'Caution is advised if nintedanib is prescribed in combination with P-gp and CYP450 3A4 inhibitors.', NULL, 'Caution is advised if nintedanib is prescribed in combination with P-gp and CYP450 3A4 inhibitors. Pharmacologic response to nintedanib should be monitored more closely whenever a CYP450 3A4/P-gp inhibitor is added to or withdrawn from therapy, and the nintedanib dosing adjusted or interrupted as necessary in accordance with the product labeling. Patients should be closely monitored for increased adverse effects such as liver enzyme and bilirubin elevations, diarrhea, nausea, vomiting, gastrointestinal perforation, bleeding, and arterial thromboembolic events (e.g., myocardial infarction).', 'Metabolism', 'Felodipine, Amlodipine, Nisoldipine, Nicardipine, Isradipine, Clevidipine, Levamlodipine, Celecoxib, Nimodipine, Amlodipine, Felodipine, More', 'Dacomitinib, Tepotinib, Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/8909/', '[1] "Product Information. Ofev (nintedanib)." Boehringer Ingelheim, Ridgefield, CT.[2] "Product Information. Ofev (nintedanib)." Boehringer Ingelheim, Ridgefield, CT.', 1767369485);
