INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (9631, 'Methylphenidate', 'Armodafinil', 'Minor', 'In single-dose pharmacokinetic studies, healthy volunteers given modafinil (200 mg) with dextroamphetamine (10 mg) or methylphenidate (40 mg) exhibited a delay in the absorption of modafinil by approximately 1 hour. The mechanism of interaction is unknown. No other alterations in the pharmacokinetics of any of the drugs were observed. Although not studied, the interaction is likely to also occur with the various prodrug, racemic, and enantiomeric formulations of these drugs (e.g., amphetamine, lisdexamfetamine, dexmethylphenidate, armodafinil).', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/9635/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[3] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.[4] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol interactions in humans." Clin Pharmacol Ther 57 (1995): 559-68[5] Jiao X, Velez S, Ringstad J, Eyma V, Miller D, Bleiberg M "Myocardial infarction associated with Adderall XR and alcohol use in a young man." J Am Board Fam Med 22 (2009): 197-201[6] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[7] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.', 1767365203);
